import * as base64 from "./base64";
import { EventTarget } from "./eventtarget";
import * as utils from "./utils";
import * as anim from "./anim";
import { Link } from "./core_link";
import { Port } from "./core_port";
import { Op } from "./core_op";
import { EMBED } from "./embedding";
import { Profiler } from "./core_profiler";
import Patch from "./core_patch";
import { Requirements } from "./requirements";
import { GLGUI } from "./glpatch";
import { Instancing } from "./instancing";
import { LoadingStatus } from "./loadingstatus";
import { WEBAUDIO } from "./webaudio";
import { Variable } from "./sessionvar";
import { Timer, now, internalNow } from "./timer";
import * as PatchConnections from "./patchConnection";
// import { Gizmo, htmlLine } from "./mod_gizmo";
import { CONSTANTS } from "./constants";

const CABLES = Object.assign(
    {
        EventTarget,
        EMBED,
        Link,
        Port,
        Op,
        Profiler,
        Requirements,
        Patch,
        GLGUI,
        // Gizmo,
        // htmlLine,
        Instancing,
        Timer,
        WEBAUDIO,
        Variable,
        LoadingStatus,
        now,
        internalNow,
    },
    base64,
    utils,
    anim,
    PatchConnections,
    CONSTANTS.PORT,
    CONSTANTS.PACO,
    CONSTANTS.ANIM,
    CONSTANTS.OP,
);

export default CABLES;

export const base64Chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";

// Use a lookup table to find the index.
const _base64lookup = new Uint8Array(256);
for (var i = 0; i < base64Chars.length; i++) _base64lookup[base64Chars.charCodeAt(i)] = i;

export const base64lookup = _base64lookup;

export const b64encTypesArray = function (arraybuffer)
{
    if (arraybuffer.buffer) arraybuffer = arraybuffer.buffer;
    var bytes = new Uint8Array(arraybuffer),
        i,
        len = bytes.length,
        base64 = "";

    for (i = 0; i < len; i += 3)
    {
        base64 += base64Chars[bytes[i] >> 2];
        base64 += base64Chars[((bytes[i] & 3) << 4) | (bytes[i + 1] >> 4)];
        base64 += base64Chars[((bytes[i + 1] & 15) << 2) | (bytes[i + 2] >> 6)];
        base64 += base64Chars[bytes[i + 2] & 63];
    }

    if (len % 3 === 2) base64 = base64.substring(0, base64.length - 1) + "=";
    else if (len % 3 === 1) base64 = base64.substring(0, base64.length - 2) + "==";

    return base64;
};

export const b64decTypedArray = function (base64)
{
    var bufferLength = base64.length * 0.75,
        len = base64.length,
        i,
        p = 0,
        encoded1,
        encoded2,
        encoded3,
        encoded4;

    if (base64[base64.length - 1] === "=")
    {
        bufferLength--;
        if (base64[base64.length - 2] === "=") bufferLength--;
    }

    var arraybuffer = new ArrayBuffer(bufferLength),
        bytes = new Uint8Array(arraybuffer);

    for (i = 0; i < len; i += 4)
    {
        encoded1 = base64lookup[base64.charCodeAt(i)];
        encoded2 = base64lookup[base64.charCodeAt(i + 1)];
        encoded3 = base64lookup[base64.charCodeAt(i + 2)];
        encoded4 = base64lookup[base64.charCodeAt(i + 3)];

        bytes[p++] = (encoded1 << 2) | (encoded2 >> 4);
        bytes[p++] = ((encoded2 & 15) << 4) | (encoded3 >> 2);
        bytes[p++] = ((encoded3 & 3) << 6) | (encoded4 & 63);
    }

    return arraybuffer;
};

const EventTarget = function ()
{
    this._eventCallbacks = {};

    this.addEventListener = function (which, cb)
    {
        if (!this._eventCallbacks[which]) this._eventCallbacks[which] = [cb];
        else this._eventCallbacks[which].push(cb);
    };

    this.hasEventListener = function (which, cb)
    {
        if (which && cb)
        {
            if (this._eventCallbacks[which])
            {
                var idx = this._eventCallbacks[which].indexOf(cb);
                if (idx == -1) return false;
                return true;
            }
        }
        else
        {
            console.log("hasListener: missing parameters");
        }
    };

    this.removeEventListener = function (which, cb)
    {
        if (this._eventCallbacks[which])
        {
            var idx = this._eventCallbacks[which].indexOf(cb);
            if (idx == -1) console.log("eventlistener " + which + " not found...");
            else this._eventCallbacks[which].splice(idx);
        }
    };

    this.emitEvent = function (which, param1, param2, param3, param4, param5, param6)
    {
        if (this._eventCallbacks[which])
        {
            for (var i = 0; i < this._eventCallbacks[which].length; i++)
            {
                if (this._eventCallbacks[which][i])
                {
                    this._eventCallbacks[which][i](param1, param2, param3, param4, param5, param6);
                }
            }
        }
        else
        {
            // console.log("has no event callback",which,this._eventCallbacks);
        }
    };
};
export { EventTarget };

/**
 * @external CABLES
 * @namespace Utils
 */

const UTILS = {};
/**
 * Merge two Float32Arrays.
 * @function float32Concat
 * @memberof Utils
 * @param {Float32Array} first Left-hand side array
 * @param {Float32Array} second Right-hand side array
 * @return {Float32Array}
 * @static
 */
UTILS.float32Concat = function (first, second)
{
    if (!(first instanceof Float32Array)) first = new Float32Array(first);
    if (!(second instanceof Float32Array)) second = new Float32Array(second);

    var firstLength = first.length,
        result = new Float32Array(firstLength + second.length);

    result.set(first);
    result.set(second, firstLength);

    return result;
};

/**
 * randomize order of an array
 * @function shuffleArray
 * @memberof Utils
 * @param {Array|Float32Array} array {Array} original
 * @return {Array|Float32Array} shuffled array
 * @static
 */
export const shuffleArray = function (array)
{
    for (var i = array.length - 1; i > 0; i--)
    {
        var j = Math.floor(Math.seededRandom() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return array;
};

const _uuid = function ()
{
    var d = new Date().getTime();
    var uuid = "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, (c) =>
    {
        var r = (d + Math.random() * 16) % 16 | 0;
        d = Math.floor(d / 16);
        return (c == "x" ? r : (r & 0x3) | 0x8).toString(16);
    });
    return uuid;
};
/**
 * generate a UUID
 * @function uuid
 * @memberof Utils
 * @return {String} generated UUID
 * @static
 */
export const uuid = _uuid;
export const generateUUID = _uuid;

/**
 * generate a simple ID
 * @function simpleId
 * @memberof Utils
 * @return {Number} new id
 * @static
 */
var simpleIdCounter = 0;
export const simpleId = function ()
{
    simpleIdCounter++;
    return simpleIdCounter;
};

/**
 * smoothStep a value
 * @function smoothStep
 * @memberof Utils
 * @function
 * @param {Number} value value to be smoothed [0-1]
 * @return {Number} smoothed value
 * @static
 */
export const smoothStep = function (perc)
{
    var x = Math.max(0, Math.min(1, (perc - 0) / (1 - 0)));
    perc = x * x * (3 - 2 * x); // smoothstep
    return perc;
};

/**
 * smootherstep a value
 * @function smootherStep
 * @memberof Utils
 * @param value {Number} value to be smoothed [0-1]
 * @return {Number} smoothed value
 * @static
 */
export const smootherStep = function (perc)
{
    var x = Math.max(0, Math.min(1, (perc - 0) / (1 - 0)));
    perc = x * x * x * (x * (x * 6 - 15) + 10); // smootherstep
    return perc;
};

/**
 * map a value in a range to a value in another range
 * @function map
 * @memberof Utils
 * @param {Number} value value to be mapped
 * @param {Number} oldMin old range minimum value
 * @param {Number} oldMax old range maximum value
 * @param {Number} newMin new range minimum value
 * @param {Number} newMax new range maximum value
 * @return {Number} mapped value
 * @static
 */
export const map = function (x, _oldMin, _oldMax, _newMin, _newMax, _easing)
{
    if (x >= _oldMax) return _newMax;
    if (x <= _oldMin) return _newMin;

    var reverseInput = false;
    var oldMin = Math.min(_oldMin, _oldMax);
    var oldMax = Math.max(_oldMin, _oldMax);
    if (oldMin != _oldMin) reverseInput = true;

    var reverseOutput = false;
    var newMin = Math.min(_newMin, _newMax);
    var newMax = Math.max(_newMin, _newMax);
    if (newMin != _newMin) reverseOutput = true;

    var portion = 0;
    var r = 0;

    if (reverseInput) portion = ((oldMax - x) * (newMax - newMin)) / (oldMax - oldMin);
    else portion = ((x - oldMin) * (newMax - newMin)) / (oldMax - oldMin);

    if (reverseOutput) r = newMax - portion;
    else r = portion + newMin;

    if (!_easing) return r;
    if (_easing == 1)
    {
        // smoothstep
        x = Math.max(0, Math.min(1, (r - _newMin) / (_newMax - _newMin)));
        return _newMin + x * x * (3 - 2 * x) * (_newMax - _newMin);
    }
    if (_easing == 2)
    {
        // smootherstep
        x = Math.max(0, Math.min(1, (r - _newMin) / (_newMax - _newMin)));
        return _newMin + x * x * x * (x * (x * 6 - 15) + 10) * (_newMax - _newMin);
    }

    return r;
};

/**
 * @namespace Math
 */
/**
 * set random seed for seededRandom()
 * @memberof Math
 * @type Number
 * @static
 */
Math.randomSeed = 1;

/**
 * generate a seeded random number
 * @function seededRandom
 * @memberof Math
 * @param {Number} max minimum possible random number
 * @param {Number} min maximum possible random number
 * @return {Number} random value
 * @static
 */
Math.seededRandom = function (max, min)
{
    if (Math.randomSeed === 0) Math.randomSeed = Math.random() * 999;
    max = max || 1;
    min = min || 0;

    Math.randomSeed = (Math.randomSeed * 9301 + 49297) % 233280;
    var rnd = Math.randomSeed / 233280.0;

    return min + rnd * (max - min);
};

// ----------------------------------------------------------------

UTILS.arrayWriteToEnd = function (arr, v)
{
    for (var i = 1; i < arr.length; i++) arr[i - 1] = arr[i];
    arr[arr.length - 1] = v;
};

// ----------------------------------------------------------------

/**
 * returns true if parameter is a number
 * @function isNumeric
 * @memberof Utils
 * @param {Any} value The value to check.
 * @return {Boolean}
 * @static
 */
UTILS.isNumeric = function (n)
{
    return !isNaN(parseFloat(n)) && isFinite(n);
};

/**
 * returns true if parameter is array
 * @function isArray
 * @param {Any} value Value to check
 * @memberof Utils
 * @return {Boolean}
 * @static
 */
UTILS.isArray = function (v)
{
    return Object.prototype.toString.call(v) === "[object Array]";
};

/**
 * @namespace String
 */

/**
 * append a linebreak to a string
 * @function endl
 * @memberof String
 * @return {String} string with newline break appended ('\n')
 */
String.prototype.endl = function ()
{
    return this + "\n";
};

/**
 * return true if string starts with prefix
 * @function startsWith
 * @memberof String
 * @param {String} prefix The prefix to check.
 * @return {Boolean}
 */
String.prototype.startsWith = function (prefix)
{
    return this.indexOf(prefix) === 0;
};

/**
 * return true if string ends with suffix
 * @function endsWith
 * @memberof String
 * @param {String} suffix
 * @return {Boolean}
 */
String.prototype.endsWith = function (suffix)
{
    return this.match(suffix + "$") == suffix;
};

// ----------------------------------------------------------------

/**
 * append a unique/random parameter to a url, so the browser is forced to reload the file, even if its cached
 * @function cacheBust
 * @static
 * @memberof Utils
 * @param {String} url The url to append the cachebuster parameter to.
 * @return {String} url with cachebuster parameter
 */
export const cacheBust = function (url)
{
    if (url.indexOf("?") > -1) url += "&";
    else url += "?";
    return url + "cb=" + CABLES.uuid();
};

var jsonpCounter = null;
export const jsonp = function (url, cb)
{
    jsonpCounter = jsonpCounter || 0;
    jsonpCounter++;
    var jsonPID = jsonpCounter;

    console.log("making jsonp request...");

    CABLES["jsonpFunc" + jsonPID] = function (data)
    {
        console.log(data);
        cb(false, data);
    };

    var paramChar = "?";
    if (url.indexOf(paramChar) > -1) paramChar = "&";

    var s = document.createElement("script");
    s.setAttribute("src", url + paramChar + "callback=CABLES.jsonpFunc" + jsonPID);
    // s.onload=function()
    // {
    // };
    document.body.appendChild(s);
};

export const ajaxSync = function (url, cb, method, post, contenttype)
{
    request({
        url,
        cb,
        method,
        data: post,
        contenttype,
        sync: true,
    });
};

export const ajax = function (url, cb, method, post, contenttype, jsonp)
{
    request({
        url,
        cb,
        method,
        "data:": post,
        contenttype,
        sync: false,
        jsonp,
    });
};

export const request = function (options)
{
    if (!options.hasOwnProperty("asynch")) options.asynch = true;

    var xhr;
    try
    {
        xhr = new XMLHttpRequest();
    }
    catch (e) {}

    xhr.onreadystatechange = function ()
    {
        if (xhr.readyState != 4) return;

        if (options.cb)
        {
            if (xhr.status == 200 || xhr.status == 0) options.cb(false, xhr.responseText, xhr);
            else options.cb(true, xhr.responseText, xhr);
        }
    };

    xhr.addEventListener("progress", (ev) =>
    {
        // console.log('progress',ev.loaded/1024+' kb');
        // if (ev.lengthComputable)
        // {
        //     var percentComplete = ev.loaded / ev.total;
        //     console.log(url,percentComplete);
        // }
    });

    xhr.open(options.method ? options.method.toUpperCase() : "GET", options.url, !options.sync);

    if (!options.post && !options.data)
    {
        xhr.send();
    }
    else
    {
        xhr.setRequestHeader(
            "Content-type",
            options.contenttype ? options.contenttype : "application/x-www-form-urlencoded",
        );
        xhr.send(options.data || options.post);
    }
};

// ----------------------------------------------------------------

window.performance = window.performance || {
    offset: Date.now(),
    now: function now()
    {
        return Date.now() - this.offset;
    },
};

export { UTILS };

import { EventTarget } from "./eventtarget";
import { generateUUID } from "./utils";
import { Anim } from "./anim";
import { CONSTANTS } from "./constants";
/**
 * data is coming into and out of ops through input and output ports
 * @external CABLES
 * @namespace Port
 * @class
 * @hideconstructor
 * @example
 * const myPort=op.inString("String Port");
 */


const Port = function (__parent, name, type, uiAttribs)
{
    EventTarget.apply(this);

    this.data = {}; // reserved for port-specific user-data
    /**
     * @type {Number}
     * @name direction
     * @instance
     * @memberof Port
     * @description direction of port (input(0) or output(1))
     */
    this.direction = CONSTANTS.PORT.PORT_DIR_IN;
    this.id = generateUUID();
    this.parent = __parent;

    /**
     * @type {Array<Link>}
     * @name links
     * @instance
     * @memberof Port
     * @description links of port
     */
    this.links = [];
    this.value = 0.0;
    this.name = name;
    this.type = type || CONSTANTS.OP.OP_PORT_TYPE_VALUE;
    this.uiAttribs = uiAttribs || {};
    this.anim = null;
    this._oldAnimVal = -5711;
    this.defaultValue = null;

    this._uiActiveState = true;
    this.ignoreValueSerialize = false;
    // this.onLink=null;
    this.onLinkChanged = null;
    this.crashed = false;

    this._valueBeforeLink = null;
    this._lastAnimFrame = -1;
    this._animated = false;

    this.onValueChanged = null;
    this.onTriggered = null;
    this.onUiActiveStateChange = null;
    this.changeAlways = false;

    this._warnedDeprecated = false;

    // this.onUiAttrChange=null;

    Object.defineProperty(this, "val", {
        get()
        {
            this._warnedDeprecated = true;
            return this.get();
        },
        set(v)
        {
            this.setValue(v);
            // if(!this._warnedDeprecated)console.log('deprecated .val set used',this.parent.name);
            this._warnedDeprecated = true;
        },
    });
};

/**
 * change listener for input value ports, overwrite to react to changes
 * @function onChange
 * @memberof Port
 * @instance
 * @example
 * const myPort=op.inString("MyPort");
 * myPort.onChange=function()
 * {
 *   console.log("was changed to: ",myPort.get());
 * }
 *
 */

Port.prototype.onAnimToggle = function () {};
Port.prototype._onAnimToggle = function ()
{
    this.onAnimToggle();
};

/**
 * @function hidePort
 * @memberof Port
 * @instance
 * @description hide port rectangle in op
 */
Port.prototype.hidePort = function ()
{
    this.setUiAttribs({ hidePort: true });
};

/**
 * @function remove
 * @memberof Port
 * @instance
 * @description remove port
 */
Port.prototype.remove = function ()
{
    // this.setUiAttribs({hidePort:true});
    this.removeLinks();
    this.parent.removePort(this);
};

/**
 * set ui attributes
 * @function setUiAttribs
 * @memberof Port
 * @instance
 * @param {Object} newAttribs
 * <pre>
 * title - overwrite title of port (by default this is portname)
 * greyout - port paramater will appear greyed out, can not be
 * hidePort - port will be hidden from op
 * hideParam - port params will be hidden from parameter panel
 * showIndex - only for dropdowns - show value index (e.g. `0 - normal` )
 * editorSyntax - set syntax highlighting theme for editor port
 * </pre>
 * @example
 * myPort.setUiAttribs({greyout:true});
 */
Port.prototype.setUiAttribs = function (newAttribs)
{
    if (!this.uiAttribs) this.uiAttribs = {};
    for (var p in newAttribs)
    {
        this.uiAttribs[p] = newAttribs[p];
    }
    // if(this.onUiAttrChange) this.onUiAttrChange(newAttribs);
    this.emitEvent("onUiAttrChange", newAttribs);
    // console.log("new attribs!",newAttribs);
};

/**
 * @function get
 * @memberof Port
 * @instance
 * @description get value of port
 */
Port.prototype.get = function ()
{
    if (this._animated && this._lastAnimFrame != this.parent.patch.getFrameNum())
    {
        this._lastAnimFrame = this.parent.patch.getFrameNum();
        this.value = this.anim.getValue(this.parent.patch.timer.getTime());

        // if(this._oldAnimVal!=this.value)
        {
            this._oldAnimVal = this.value;
            this.forceChange();
        }
    }

    return this.value;
};

/**
 * @function setValue
 * @memberof Port
 * @instance
 * @description set value of port / will send value to all linked ports (only for output ports)
 */
Port.prototype.set = Port.prototype.setValue = function (v)
{
    if (v === undefined) return;

    if (this.parent.enabled && !this.crashed)
    {
        if (v !== this.value || this.changeAlways || this.type == CONSTANTS.OP.OP_PORT_TYPE_TEXTURE || this.type == CONSTANTS.OP.OP_PORT_TYPE_ARRAY)
        {
            if (this._animated)
            {
                this.anim.setValue(this.parent.patch.timer.getTime(), v);
            }
            else
            {
                try
                {
                    this.value = v;
                    this.forceChange();
                }
                catch (ex)
                {
                    this.crashed = true;
                    this.setValue = function (v) {};
                    this.onTriggered = function () {};

                    console.log("exception!");
                    console.error("onvaluechanged exception cought", ex);
                    console.log(ex.stack);
                    console.log("exception in: " + this.parent.name);
                    if (gui) gui.showOpCrash(this.parent);

                    this.parent.patch.emitEvent("exception".ex,this.parent);
                }

                if (CABLES.UI && this.type == CONSTANTS.OP.OP_PORT_TYPE_TEXTURE)
                {
                    gui.texturePreview().updateTexturePort(this);
                }
            }

            if (this.direction == CONSTANTS.PORT.PORT_DIR_OUT) for (var i = 0; i < this.links.length; ++i) this.links[i].setValue();
        }
    }
};

Port.prototype.updateAnim = function ()
{
    if (this._animated)
    {
        this.value = this.get();

        if (this._oldAnimVal != this.value || this.changeAlways)
        {
            this._oldAnimVal = this.value;
            this.forceChange();
        }
        this._oldAnimVal = this.value;
    }
};

Port.args = function (func)
{
    return (func + "")
        .replace(/[/][/].*$/gm, "") // strip single-line comments
        .replace(/\s+/g, "") // strip white space
        .replace(/[/][*][^/*]*[*][/]/g, "") // strip multi-line comments
        .split("){", 1)[0]
        .replace(/^[^(]*[(]/, "") // extract the parameters
        .replace(/=[^,]+/g, "") // strip any ES6 defaults
        .split(",")
        .filter(Boolean); // split & filter [""]
};

Port.prototype.forceChange = function ()
{
    if (this.onValueChanged || this.onChange)
    {
        // very temporary: deprecated warning!!!!!!!!!
        // var params=Port.args(this.onValueChanged||this.onChange)
        // if(params.length>0) console.warn('TOM: port has onchange params!',this.parent.objName,this.name);
    }

    if (this.onChange) this.onChange(this, this.value);
    else if (this.onValueChanged) this.onValueChanged(this, this.value); // deprecated
};

/**
 * @function getTypeString
 * @memberof Port
 * @instance
 * @description get port type as string, e.g. "Function","Value"...
 * @return {String} type
 */
Port.prototype.getTypeString = function ()
{
    if (this.type == CONSTANTS.OP.OP_PORT_TYPE_VALUE) return "Number";
    if (this.type == CONSTANTS.OP.OP_PORT_TYPE_FUNCTION) return "Trigger";
    if (this.type == CONSTANTS.OP.OP_PORT_TYPE_OBJECT) return "Object";
    if (this.type == CONSTANTS.OP.OP_PORT_TYPE_DYNAMIC) return "Dynamic";
    if (this.type == CONSTANTS.OP.OP_PORT_TYPE_ARRAY) return "Array";
    if (this.type == CONSTANTS.OP.OP_PORT_TYPE_STRING) return "String";
    return "Unknown";
};

Port.prototype.getSerialized = function ()
{
    var obj = {};
    obj.name = this.getName();

    if (!this.ignoreValueSerialize && this.links.length === 0)
    {
        if (this.type == CONSTANTS.OP.OP_PORT_TYPE_OBJECT && this.value && this.value.tex)
        {
        }
        else obj.value = this.value;
    }
    if (this._animated) obj.animated = true;
    if (this.anim) obj.anim = this.anim.getSerialized();
    if (this.uiAttribs.display == "file") obj.display = this.uiAttribs.display;
    if (this.direction == CONSTANTS.PORT.PORT_DIR_IN && this.links.length > 0)
    {
        obj.links = [];
        for (var i in this.links)
        {
            if (this.links[i].portIn && this.links[i].portOut) obj.links.push(this.links[i].getSerialized());
        }
    }
    return obj;
};

Port.prototype.shouldLink = function ()
{
    return true;
};

/**
 * @function removeLinks
 * @memberof Port
 * @instance
 * @description remove all links from port
 */
Port.prototype.removeLinks = function ()
{
    var count = 0;
    while (this.links.length > 0)
    {
        count++;
        if (count > 5000)
        {
            console.warn("could not delete links... / infinite loop");
            this.links.length = 0;
            break;
        }
        this.links[0].remove();
    }
};

/**
 * @function removeLink
 * @memberof Port
 * @instance
 * @description remove all link from port
 * @param {CABLES.Link} link
 */
Port.prototype.removeLink = function (link)
{
    for (var i in this.links)
    {
        if (this.links[i] == link)
        {
            this.links.splice(i, 1);
        }
    }

    if (this.direction == CONSTANTS.PORT.PORT_DIR_IN)
    {
        if (this.type == CONSTANTS.OP.OP_PORT_TYPE_VALUE) this.setValue(this._valueBeforeLink || 0);
        else this.setValue(this._valueBeforeLink || null);
    }

    // if (this.type == CABLES.CONSTANTS.OP.OP_PORT_TYPE_OBJECT && this.direction == CABLES.CONSTANTS.PORT.PORT_DIR_IN && this.links.length > 0)
    // {
    //     console.log("REMOVELINK OBJECT!!",this);

    //     for (var i=0;i<this.links.length;i++)
    //     {
    //         // console.log('iii', i, this.links[i].portOut.get());
    //         // this.links[i].setValue();
    //         // this.set(null);
    //         // this.forceChange();
    //         this.set(this.links[i].portOut.get());
    //         console.log(this.get())
    //         // this.forceChange();

    //     }
    // }

    if (this.onLinkChanged) this.onLinkChanged();
    this.emitEvent("onLinkChanged");
};

/**
 * @function getName
 * @memberof Port
 * @instance
 * @description return port name
 */
Port.prototype.getName = function ()
{
    return this.name;
};

Port.prototype.addLink = function (l)
{
    this._valueBeforeLink = this.value;
    this.links.push(l);
    if (this.onLinkChanged) this.onLinkChanged();
    this.emitEvent("onLinkChanged");
};

/**
 * @function getLinkTo
 * @memberof Port
 * @instance
 * @param {Port} otherPort
 * @description return link, which is linked to otherPort
 */
Port.prototype.getLinkTo = function (p2)
{
    for (var i in this.links) if (this.links[i].portIn == p2 || this.links[i].portOut == p2) return this.links[i];
};

/**
 * @function removeLinkTo
 * @memberof Port
 * @instance
 * @param {Port} otherPort
 * @description removes link, which is linked to otherPort
 */
Port.prototype.removeLinkTo = function (p2)
{
    for (var i in this.links)
    {
        if (this.links[i].portIn == p2 || this.links[i].portOut == p2)
        {
            this.links[i].remove();
            if (this.onLinkChanged) this.onLinkChanged();
            this.emitEvent("onLinkChanged");
            return;
        }
    }
};

/**
 * @function isLinkedTo
 * @memberof Port
 * @instance
 * @param {Port} otherPort
 * @description returns true if port is linked to otherPort
 */
Port.prototype.isLinkedTo = function (p2)
{
    for (var i in this.links) if (this.links[i].portIn == p2 || this.links[i].portOut == p2) return true;

    return false;
};

/**
 * @function trigger
 * @memberof Port
 * @instance
 * @description trigger the linked port (usually invoked on an output function port)
 */
Port.prototype.trigger = function ()
{
    if (this.links.length === 0) return;
    if (!this.parent.enabled) return;

    var portTriggered = null;
    try
    {
        for (var i = 0; i < this.links.length; ++i)
        {
            if (this.links[i].portIn)
            {
                portTriggered = this.links[i].portIn;
                portTriggered._onTriggered();
            }
            if (this.links[i]) this.links[i].activity();
        }
    }
    catch (ex)
    {
        this.parent.enabled = false;

        if (CABLES.UI)
        {
            this.parent.patch.emitEvent("exception".ex,portTriggered.parent);

            if (window.gui) gui.showOpCrash(portTriggered.parent);
        }
        console.log("exception!");
        console.error("ontriggered exception cought", ex);
        console.log(ex.stack);
        console.log("exception in: " + portTriggered.parent.name);
    }
};

Port.prototype.call = function ()
{
    console.log("call deprecated - use trigger() ");
    this.trigger();
};

Port.prototype.execute = function ()
{
    console.log("### execute port: " + this.getName(), this.goals.length);
};

Port.prototype.setAnimated = function (a)
{
    if (this._animated != a)
    {
        this._animated = a;
        if (this._animated && !this.anim) this.anim = new Anim();
        this._onAnimToggle();
    }
};

Port.prototype.toggleAnim = function (val)
{
    this._animated = !this._animated;
    if (this._animated && !this.anim) this.anim = new Anim();
    this.setAnimated(this._animated);
    this._onAnimToggle();
};

/**
 * <pre>
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_VALUE = 0;
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_FUNCTION = 1;
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_OBJECT = 2;
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_TEXTURE = 2;
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_ARRAY = 3;
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_DYNAMIC = 4;
 * CABLES.CONSTANTS.OP.OP_PORT_TYPE_STRING = 5;
 * </pre>
 * @function getType
 * @memberof Port
 * @instance
 * @return {Number} type of port
 */
Port.prototype.getType = function ()
{
    return this.type;
};

/**
 * @function isLinked
 * @memberof Port
 * @instance
 * @return {Boolean} true if port is linked
 */
Port.prototype.isLinked = function ()
{
    return this.links.length > 0;
};

/**
 * @function isAnimated
 * @memberof Port
 * @instance
 * @return {Boolean} true if port is animated
 */
Port.prototype.isAnimated = function ()
{
    return this._animated;
};

/**
 * @function isHidden
 * @memberof Port
 * @instance
 * @return {Boolean} true if port is hidden
 */
Port.prototype.isHidden = function ()
{
    return this.uiAttribs.hidePort;
};

/**
 * @function onTriggered
 * @memberof Port
 * @instance
 * @param {onTriggeredCallback} callback
 * @description set callback, which will be executed when port was triggered (usually output port)
 */
Port.prototype._onTriggered = function ()
{
    this.parent.updateAnims();
    if (this.parent.enabled && this.onTriggered) this.onTriggered();
};

Port.prototype._onTriggeredProfiling = function ()
{
    this.parent.updateAnims();
    this.parent.patch.profiler.add("port", this);

    if (this.parent.enabled && this.onTriggered) this.onTriggered();
    this.parent.patch.profiler.add("port", null);
};

Port.prototype.onValueChange = function (cb)
{
    // deprecated
    this.onChange = cb;
};

Port.prototype.getUiActiveState = function ()
{
    return this._uiActiveState;
};

Port.prototype.setUiActiveState = function (onoff)
{
    this._uiActiveState = onoff;
    if (this.onUiActiveStateChange) this.onUiActiveStateChange();
};

/**
 * Returns the port type string, e.g. "value" based on the port type number
 * @function portTypeNumberToString
 * @instance
 * @memberof Port
 * @param {Number} type - The port type number
 * @returns {String} - The port type as string
 */
Port.portTypeNumberToString = function (type)
{
    if (type == CONSTANTS.OP.OP_PORT_TYPE_VALUE) return "value";
    if (type == CONSTANTS.OP.OP_PORT_TYPE_FUNCTION) return "function";
    if (type == CONSTANTS.OP.OP_PORT_TYPE_OBJECT) return "object";
    if (type == CONSTANTS.OP.OP_PORT_TYPE_ARRAY) return "array";
    if (type == CONSTANTS.OP.OP_PORT_TYPE_STRING) return "string";
    if (type == CONSTANTS.OP.OP_PORT_TYPE_DYNAMIC) return "dynamic";
    return "unknown";
};

