const LoadingStatus = function (patch)
{
    this._loadingAssets = {};
    this._cbFinished = [];
    this._percent = 0;
    this._count = 0;
    this._countFinished = 0;
    this._order = 0;
    this._startTime = 0;
    this._patch = patch;
};

LoadingStatus.prototype.setOnFinishedLoading = function (cb)
{
    this._cbFinished.push(cb);
};

LoadingStatus.prototype.getNumAssets = function ()
{
    return this._countFinished;
};

LoadingStatus.prototype.getProgress = function ()
{
    return this._percent;
};

LoadingStatus.prototype.checkStatus = function ()
{
    this._countFinished = 0;
    this._count = 0;

    for (var i in this._loadingAssets)
    {
        this._count++;
        if (!this._loadingAssets[i].finished)
        {
            this._countFinished++;
        }
    }

    this._percent = (this._count - this._countFinished) / this._count;

    // if (onLoadingAssetsFinished)
    // {
    //     console.error("CGL.onLoadingAssetsFinished is deprecated, please use config parameter onFinishedLoading with scene/patch constructor");
    //     setTimeout(this._cbFinished, 200);
    // }

    if (this._countFinished === 0)
    {
        for (var j = 0; j < this._cbFinished.length; j++)
        {
            setTimeout(this._cbFinished[j], 200);
        }
        this.print();
    }
};

LoadingStatus.prototype.print = function ()
{
    if (this._patch.silent) return;

    var rows = [];

    for (var i in this._loadingAssets)
    {
        rows.push([
            this._loadingAssets[i].order,
            this._loadingAssets[i].type,
            this._loadingAssets[i].name,
            (this._loadingAssets[i].timeEnd - this._loadingAssets[i].timeStart) / 1000 + "s",
        ]);
    }

    console.groupCollapsed(
        "finished loading " + this._order + " assets in " + (Date.now() - this._startTime) / 1000 + "s",
    );
    console.table(rows);
    console.groupEnd();
};

LoadingStatus.prototype.finished = function (id)
{
    if (this._loadingAssets[id])
    {
        this._loadingAssets[id].finished = true;
        this._loadingAssets[id].timeEnd = Date.now();
    }

    this.checkStatus();
};

LoadingStatus.prototype.start = function (type, name)
{
    if (this._startTime == 0) this._startTime = Date.now();
    var id = generateUUID();

    this._loadingAssets[id] = {
        id,
        type,
        name,
        finished: false,
        timeStart: Date.now(),
        order: this._order,
    };
    this._order++;

    return id;
};

export { LoadingStatus };

// todo: needs to be removed...

const Instancing = function ()
{
    this._loops = [];
    this._indizes = [];
    this._index = 0;
};

Instancing.prototype.pushLoop = function (maxNum)
{
    this._loops.push(Math.abs(Math.floor(maxNum)));
    this._indizes.push(this._index);
};

Instancing.prototype.popLoop = function ()
{
    this._loops.pop();
    // this._index--;
    this._index = this._indizes.pop();
    if (this._loops.length === 0) this._index = 0;
};

Instancing.prototype.numLoops = function ()
{
    return this._loops.length;
};

Instancing.prototype.numCycles = function ()
{
    if (this._loops.length === 0) return 0;
    var num = this._loops[0];
    for (var i = 1; i < this._loops.length; i++) num *= this._loops[i];

    return num;
};

Instancing.prototype.inLoop = function ()
{
    return this._loops.length > 0;
};

Instancing.prototype.increment = function ()
{
    this._index++;
};

Instancing.prototype.index = function ()
{
    return this._index;
};

export { Instancing };

import { now } from "./timer";

const Profiler = function ()
{
    var items = {};
    var currentId = null;
    var currentStart = 0;

    this.getItems = function ()
    {
        return items;
    };

    this.clear = function ()
    {
        items = {};
    };

    this.add = function (type, object)
    {
        if (currentId !== null)
        {
            if (!object || object.id != currentId)
            {
                if (items[currentId])
                {
                    // console.log(currentStart);
                    items[currentId].timeUsed += performance.now() - currentStart;

                    if (!items[currentId].peakTime || now() - items[currentId].peakTime > 5000)
                    {
                        if (items[currentId].peak > 1 && object)
                        {
                            console.log("PEAK ", object.parent.objName);
                        }

                        items[currentId].peak = 0;
                        items[currentId].peakTime = now();
                    }
                    items[currentId].peak = Math.max(items[currentId].peak, performance.now() - currentStart);
                }
            }
        }

        if (object !== null)
        {
            if (!items[object.id])
            {
                items[object.id] = {
                    numTriggers: 0,
                    timeUsed: 0,
                };
            }

            items[object.id].numTriggers++;
            items[object.id].title = object.parent.name + object.name;

            currentId = object.id;
            currentStart = performance.now();
        }
        else
        {
            currentId = null;
        }
    };

    this.print = function ()
    {
        console.log("--------");
        for (var i in items)
        {
            console.log(items[i].title + ": " + items[i].numTriggers + " / " + items[i].timeUsed);
        }
    };
};

export { Profiler };

const Requirements = function (patch)
{
    this._patch = patch;
    this.result = [];
};

Requirements.MIDI = 0;
Requirements.POINTERLOCK = 1;
Requirements.WEBAUDIO = 2;
Requirements.WEBGL2 = 3;

Requirements.infos = [];
Requirements.infos[Requirements.POINTERLOCK] = { title: "pointerLock", caniuse: "https://caniuse.com/#search=pointerlock" };
Requirements.infos[Requirements.MIDI] = { title: "midi API", caniuse: "https://caniuse.com/#search=midi" };
Requirements.infos[Requirements.WEBAUDIO] = { title: "web audio", caniuse: "https://caniuse.com/#search=webaudio" };
Requirements.infos[Requirements.WEBGL2] = { title: "WebGL 2" };

Requirements.prototype.checkRequirement = function (which, op)
{
    this.result = [];
    switch (which)
    {
    case Requirements.WEBGL2:
        return op.patch.cgl.glVersion >= 2;
        break;
    case Requirements.POINTERLOCK:
        return "exitPointerLock" in document;
        break;
    case Requirements.MIDI:
        return "MIDIAccess" in window;
        break;
    case Requirements.WEBAUDIO:
        var has = false;
        if (window.audioContext) has = true;
        if (!has && ("webkitAudioContext" in window || "AudioContext" in window)) has = true;
        return has;
        break;
    }
};

Requirements.prototype.checkOp = function (op)
{
    if (op && op.requirements)
    {
        for (var j = 0; j < op.requirements.length; j++)
        {
            var reqId = op.requirements[j];
            if (!this.result[reqId])
            {
                var success = this.checkRequirement(reqId, op);

                if (!success)
                {
                    if (op.patch.cgl && op.patch.cgl.canvas) op.patch.cgl.canvas.remove();
                    var title = Requirements.infos[reqId].title;
                    if (Requirements.infos[reqId].caniuse) title = "<a href=\"" + Requirements.infos[reqId].caniuse + "\" target=\"_blank\">" + Requirements.infos[reqId].title + " (" + op.objName + ")</a>";
                    // document.writeln('<pre>browser does not meet requirement: '+title+'</pre>');
                    // console.error("browser does not meet requirement: "+Requirements.infos[reqId].title);
                    throw "this browser does not meet requirement: " + Requirements.infos[reqId].title + " (" + op.objName + ")";
                }

                this.result[reqId] = {
                    success,
                    info: Requirements.infos[reqId],
                };
            }
        }
    }
};

export { Requirements };

