Patch.prototype.getSubPatchOp = function (patchId, objName)
{
    for (var i in this.ops)
    {
        if (this.ops[i].uiAttribs && this.ops[i].uiAttribs.subPatch == patchId && this.ops[i].objName == objName)
        {
            return this.ops[i];
        }
    }
    return false;
