 */
Patch.Variable = function (name, val)
{
    this._name = name;
    this._changeListeners = [];
    this.setValue(val);
};

/**
 * @function Variable.getValue
 * @memberof Patch.Variable
 * @returns {String|Number|Boolean}
 */

Patch.Variable.prototype.getValue = function ()
{
    return this._v;
};

/**
 * @function getName
 * @memberof Patch.Variable
 * @instance
 * @returns {String|Number|Boolean}
