// export { Uniform };

import { Uniform } from "./cgl_shader_uniform";
import { CONSTANTS } from "./constants";
import { profileData } from "./cgl_profiledata";
import { extendMeshWithFeedback } from "./cgl_mesh_feedback";

const MESH = {};
MESH.lastMesh = null;

/**
 * webgl renderable 3d object
 * @external CGL
 * @namespace Mesh
 * @hideconstructor
 * @param {Context} cgl
 * @param {Geometry} geometry
 * @param {Number} [glPrimitive]
 * @class
 * @example
 * const cgl=op.patch.cgl
 * const mesh=new CGL.Mesh(cgl, geometry);
 *
 * function render()
 * {
 *   mesh.render(cgl.getShader());
 * }
 */
const Mesh = function (_cgl, __geom, glPrimitive)
{
    this._cgl = _cgl;
    this._bufVertexAttrib = null;
    this._bufVerticesIndizes = this._cgl.gl.createBuffer();
    this._attributes = [];
    this._attribLocs = {};
    this._geom = null;
    this._lastShader = null;
    this._numInstances = 0;
    this._glPrimitive = glPrimitive;
    this._preWireframeGeom = null;
    this.addVertexNumbers = false;
    this.feedBackAttributes = [];
    this.setGeom(__geom);

    this._feedBacks = [];
    this._feedBacksChanged = false;
    this._transformFeedBackLoc = -1;
    this._lastAttrUpdate = 0;

    Object.defineProperty(this, "numInstances", {
        get()
        {
            return this._numInstances;
        },
        set(v)
        {
            this.setNumInstances(v);
        },
    });
};

/**
 * @function updateVertices
 * @memberof Mesh
 * @instance
 * @description update vertices only from a geometry
 * @param {Geometry} geometry
 */
Mesh.prototype.updateVertices = function (geom)
{
    this.setAttribute(CONSTANTS.SHADER.SHADERVAR_VERTEX_POSITION, geom.vertices, 3);
};

Mesh.prototype.setAttributePointer = function (attrName, name, stride, offset)
{
    for (var i = 0; i < this._attributes.length; i++)
    {
        if (this._attributes[i].name == attrName)
        {
            if (!this._attributes[i].pointer) this._attributes[i].pointer = [];
            this._attributes[i].pointer.push({
                loc: -1,
                name,
                stride,
                offset,
                instanced: attrName == CONSTANTS.SHADER.SHADERVAR_INSTANCE_MMATRIX,
            });
        }
    }
};

Mesh.prototype.getAttribute = function (name)
{
    for (var i = 0; i < this._attributes.length; i++) if (this._attributes[i].name == name) return this._attributes[i];
};

/**
 * @function setAttribute
 * @description update attribute
 * @memberof Mesh
 * @instance
 * @param {String} attribute name
 * @param {Array} data
 * @param {Number} itemSize
 * @param {Object} options
 */
Mesh.prototype.addAttribute = Mesh.prototype.updateAttribute = Mesh.prototype.setAttribute = function (name, array, itemSize, options)
{
    var floatArray = null;
    var cb = null;
    var instanced = false;
    var i = 0;
    var numItems = array.length / itemSize;

    if (numItems === 0) console.warn("CGL_MESH: num items in attribute " + name + " is ZERO");

    if (typeof options == "function")
    {
        cb = options;
    }

    if (typeof options == "object")
    {
        if (options.cb) cb = options.cb;
        if (options.instanced) instanced = options.instanced;
    }

    if (name == CONSTANTS.SHADER.SHADERVAR_INSTANCE_MMATRIX) instanced = true;

    if (!(array instanceof Float32Array))
    {
        floatArray = new Float32Array(array);
        profileData.profileNonTypedAttrib++;
        profileData.profileNonTypedAttribNames = this._geom.name + " " + name;
    }
    else floatArray = array;

    for (i = 0; i < this._attributes.length; i++)
    {
        if (this._attributes[i].name == name)
        {
            this._attributes[i].numItems = numItems;
            this._cgl.gl.bindBuffer(this._cgl.gl.ARRAY_BUFFER, this._attributes[i].buffer);
            this._cgl.gl.bufferData(this._cgl.gl.ARRAY_BUFFER, floatArray, this._cgl.gl.DYNAMIC_DRAW);

            return this._attributes[i];
        }
    }

    var buffer = this._cgl.gl.createBuffer();

    this._cgl.gl.bindBuffer(this._cgl.gl.ARRAY_BUFFER, buffer);
    this._cgl.gl.bufferData(this._cgl.gl.ARRAY_BUFFER, floatArray, this._cgl.gl.DYNAMIC_DRAW);

    var type = this._cgl.gl.FLOAT;
    if (options && options.type) type = options.type;
    var attr = {
        buffer,
        name,
        cb,
        itemSize,
        numItems,
        startItem: 0,
        instanced,
        type,
    };

    if (name == CONSTANTS.SHADER.SHADERVAR_VERTEX_POSITION) this._bufVertexAttrib = attr;
    this._attributes.push(attr);

    this._attribLocs = {};
    // for(var at in this._attribLocs)
    //     for(i=0;i<this._attributes.length;i++)
    //     {
    //         this._attribLocs[at].length=0;
    //         // this._attributes[i].loc=-1;
    //     }

    return attr;
};

Mesh.prototype.getAttributes = function ()
{
    return this._attributes;
};

/**
 * @function updateTexCoords
 * @description update texture coordinates only from a geometry
 * @memberof Mesh
 * @instance
 * @param {Geometry} geometry
 */
Mesh.prototype.updateTexCoords = function (geom)
{
    if (geom.texCoords && geom.texCoords.length > 0)
    {
        this.setAttribute(CONSTANTS.SHADER.SHADERVAR_VERTEX_TEXCOORD, geom.texCoords, 2);
    }
    else
    {
        var tcBuff = new Float32Array(Math.round((geom.vertices.length / 3) * 2));
        this.setAttribute(CONSTANTS.SHADER.SHADERVAR_VERTEX_TEXCOORD, tcBuff, 2);
    }
};

Mesh.prototype._setVertexNumbers = function ()
{
    var numVerts = this._geom.vertices.length / 3;

    if (!this._verticesNumbers || this._verticesNumbers.length != numVerts)
    {
        this._verticesNumbers = new Float32Array(numVerts);

        for (var i = 0; i < numVerts; i++) this._verticesNumbers[i] = i;

        this.setAttribute(CONSTANTS.SHADER.SHADERVAR_VERTEX_NUMBER, this._verticesNumbers, 1, (attr, geom, shader) =>
        {
            if (!shader.uniformNumVertices) shader.uniformNumVertices = new Uniform(shader, "f", "numVertices", numVerts);
            shader.uniformNumVertices.setValue(numVerts);
        });
    }
};

Mesh.prototype.setVertexIndices = function (vertIndices)
{
    if (vertIndices.length > 0)
    {
        for (var i = 0; i < vertIndices.length; i++)
        {
            if (vertIndices[i] >= this._geom.vertices.length / 3)
            {
                console.warn("invalid index in " + this._geom.name);
                return;
            }
        }

        this._cgl.gl.bindBuffer(this._cgl.gl.ELEMENT_ARRAY_BUFFER, this._bufVerticesIndizes);

        // todo cache this ?
        // if(!this.vertIndicesTyped || this.vertIndicesTyped.length!=this._geom.verticesIndices.length)

        if (!(vertIndices instanceof Uint16Array)) this.vertIndicesTyped = new Uint16Array(vertIndices);
        else this.vertIndicesTyped = vertIndices;

        this._cgl.gl.bufferData(this._cgl.gl.ELEMENT_ARRAY_BUFFER, this.vertIndicesTyped, this._cgl.gl.DYNAMIC_DRAW);
        this._bufVerticesIndizes.itemSize = 1;
        this._bufVerticesIndizes.numItems = vertIndices.length;
    }
    else this._bufVerticesIndizes.numItems = 0;
};

/**
 * @function setGeom
 * @memberof Mesh
 * @instance
 * @description set geometry for mesh
 * @param {Geometry} geometry
 */
Mesh.prototype.setGeom = function (geom)
{
    this._geom = geom;

    MESH.lastMesh = null;
    profileData.profileMeshSetGeom++;

    this._disposeAttributes();
    this.updateVertices(this._geom);
    this.setVertexIndices(this._geom.verticesIndices);

    if (this._geom.vertexNormals.length > 0) this.setAttribute("attrVertNormal", this._geom.vertexNormals, 3);

    this.updateTexCoords(this._geom);

    if (this._geom.hasOwnProperty("tangents") && this._geom.tangents && this._geom.tangents.length > 0) this.setAttribute("attrTangent", this._geom.tangents, 3);
    if (this._geom.hasOwnProperty("biTangents") && this._geom.biTangents && this._geom.biTangents.length > 0) this.setAttribute("attrBiTangent", this._geom.biTangents, 3);
    if (this._geom.vertexColors.length > 0) this.setAttribute("attrVertColor", this._geom.vertexColors, 4);

    if (this.addVertexNumbers) this._setVertexNumbers();

    var geomAttribs = this._geom.getAttributes();
    for (var index in geomAttribs)
    {
        this.setAttribute(index, geomAttribs[index].data, geomAttribs[index].itemSize);
    }
};

Mesh.prototype._preBind = function (shader)
{
    for (var i = 0; i < this._attributes.length; i++)
    {
        if (this._attributes[i].cb)
        {
            this._attributes[i].cb(this._attributes[i], this._geom, shader);
        }
    }
};

Mesh.prototype._bind = function (shader)
{
    if (shader != this._lastShader) this.unBind();
    var attrLocs = [];
    if (this._attribLocs[shader.id]) attrLocs = this._attribLocs[shader.id];
    else this._attribLocs[shader.id] = attrLocs;

    this._lastShader = shader;
    var i = 0;
    if (shader.lastCompile > this._lastAttrUpdate || attrLocs.length != this._attributes.length)
    {
        this._lastAttrUpdate = shader.lastCompile;
        for (i = 0; i < this._attributes.length; i++) attrLocs[i] = -1;
    }

    for (i = 0; i < this._attributes.length; i++)
    {
        var attribute = this._attributes[i];
        if (attrLocs[i] == -1)
        {
            if (attribute._attrLocationLastShaderTime != shader.lastCompile)
            {
                attribute._attrLocationLastShaderTime = shader.lastCompile;
                attrLocs[i] = this._cgl.glGetAttribLocation(shader.getProgram(), attribute.name);
                profileData.profileAttrLoc++;
            }
        }

        if (attrLocs[i] != -1)
        {
            this._cgl.gl.enableVertexAttribArray(attrLocs[i]);
            this._cgl.gl.bindBuffer(this._cgl.gl.ARRAY_BUFFER, attribute.buffer);

            if (attribute.instanced)
            {
                // todo: easier way to fill mat4 attribs...
                if (attribute.itemSize <= 4)
                {
                    if (!attribute.itemSize || attribute.itemSize == 0) console.log("instanced attrib itemsize error", this._geom.name, attribute);

                    this._cgl.gl.vertexAttribPointer(attrLocs[i], attribute.itemSize, attribute.type, false, attribute.itemSize * 4, 0);
                    this._cgl.gl.vertexAttribDivisor(attrLocs[i], 1);
                }
                else if (attribute.itemSize == 16)
                {
                    const stride = 16 * 4;

                    this._cgl.gl.vertexAttribPointer(attrLocs[i], 4, attribute.type, false, stride, 0);
                    this._cgl.gl.enableVertexAttribArray(attrLocs[i] + 1);
                    this._cgl.gl.vertexAttribPointer(attrLocs[i] + 1, 4, attribute.type, false, stride, 4 * 4 * 1);
                    this._cgl.gl.enableVertexAttribArray(attrLocs[i] + 2);
                    this._cgl.gl.vertexAttribPointer(attrLocs[i] + 2, 4, attribute.type, false, stride, 4 * 4 * 2);
                    this._cgl.gl.enableVertexAttribArray(attrLocs[i] + 3);
                    this._cgl.gl.vertexAttribPointer(attrLocs[i] + 3, 4, attribute.type, false, stride, 4 * 4 * 3);

                    this._cgl.gl.vertexAttribDivisor(attrLocs[i], 1);
                    this._cgl.gl.vertexAttribDivisor(attrLocs[i] + 1, 1);
                    this._cgl.gl.vertexAttribDivisor(attrLocs[i] + 2, 1);
                    this._cgl.gl.vertexAttribDivisor(attrLocs[i] + 3, 1);
                }
                else
                {
                    console.log("unknown instance attrib size", attribute.name);
                }
            }
            else
            {
                if (!attribute.itemSize || attribute.itemSize == 0) console.log("attrib itemsize error", this._geom.name, attribute);
                this._cgl.gl.vertexAttribPointer(attrLocs[i], attribute.itemSize, attribute.type, false, attribute.itemSize * 4, 0);

                if (attribute.pointer)
                {
                    for (var ip = 0; ip < attribute.pointer.length; ip++)
                    {
                        var pointer = attribute.pointer[ip];

                        if (pointer.loc == -1) pointer.loc = this._cgl.glGetAttribLocation(shader.getProgram(), pointer.name);
                        profileData.profileAttrLoc++;

                        this._cgl.gl.enableVertexAttribArray(pointer.loc);
                        // this._cgl.gl.bindBuffer(this._cgl.gl.ARRAY_BUFFER, attribute.buffer);
                        this._cgl.gl.vertexAttribPointer(pointer.loc, attribute.itemSize, attribute.type, false, pointer.stride, pointer.offset);
                    }
                }
                this.bindFeedback(attribute);
            }
        }
    }

    if (this._bufVerticesIndizes.numItems !== 0) this._cgl.gl.bindBuffer(this._cgl.gl.ELEMENT_ARRAY_BUFFER, this._bufVerticesIndizes);
};

Mesh.prototype.unBind = function ()
{
    var shader = this._lastShader;
    this._lastShader = null;
    if (!shader) return;

    var attrLocs = [];
    if (this._attribLocs[shader.id]) attrLocs = this._attribLocs[shader.id];
    else this._attribLocs[shader.id] = attrLocs;

    MESH.lastMesh = null;

    for (var i = 0; i < this._attributes.length; i++)
    {
        if (this._attributes[i].instanced)
        {
            // todo: easier way to fill mat4 attribs...
            if (this._attributes[i].itemSize <= 4)
            {
                if (attrLocs[i] != -1) this._cgl.gl.vertexAttribDivisor(attrLocs[i], 0);
                if (attrLocs[i] >= 0) this._cgl.gl.disableVertexAttribArray(attrLocs[i]);
            }
            else
            {
                this._cgl.gl.vertexAttribDivisor(attrLocs[i], 0);
                this._cgl.gl.vertexAttribDivisor(attrLocs[i] + 1, 0);
                this._cgl.gl.vertexAttribDivisor(attrLocs[i] + 2, 0);
                this._cgl.gl.vertexAttribDivisor(attrLocs[i] + 3, 0);
                this._cgl.gl.disableVertexAttribArray(attrLocs[i] + 1);
                this._cgl.gl.disableVertexAttribArray(attrLocs[i] + 2);
                this._cgl.gl.disableVertexAttribArray(attrLocs[i] + 3);
            }
        }

        if (attrLocs[i] != -1) this._cgl.gl.disableVertexAttribArray(attrLocs[i]);
    }
};

Mesh.prototype.meshChanged = function ()
{
    return this._cgl.lastMesh && this._cgl.lastMesh != this;
};

Mesh.prototype.printDebug = function (shader)
{
    console.log("--attributes");
    for (i = 0; i < this._attributes.length; i++)
    {

        console.log("attribute " + i + " " + this._attributes[i].name);
    }
};

Mesh.prototype.setNumVertices = function (num)
{
    this._bufVertexAttrib.numItems = num;
};

/**
 * @function render
 * @memberof Mesh
 * @instance
 * @description draw mesh to screen
 * @param {Shader} shader
 */
Mesh.prototype.render = function (shader)
{
    // TODO: enable/disablevertex only if the mesh has changed... think drawing 10000x the same mesh

    if (!shader) return;
    var i = 0;

    if (!shader.wireframe && !this._geom.isIndexed() && this._preWireframeGeom) this.setGeom(this._preWireframeGeom);
    if (shader.wireframe && this._geom.isIndexed())
    {
        this._preWireframeGeom = this._geom;
        this._geom = this._geom.copy();
        this._geom.unIndex();
        this._geom.calcBarycentric();
        this.setGeom(this._geom);
        this.setAttribute("attrBarycentric", this._geom.barycentrics, 3);
    }

    var needsBind = false;
    if (MESH.lastMesh != this)
    {
        if (MESH.lastMesh) MESH.lastMesh.unBind();
        needsBind = true;
    }

    // var needsBind=false;
    //     {
    //     needsBind=true;
    //         }
    if (needsBind) this._preBind(shader);

    shader.bind();

    if (shader.bindTextures) shader.bindTextures();

    // if(needsBind)
    this._bind(shader);
    if (this.addVertexNumbers) this._setVertexNumbers();

    MESH.lastMesh = this;

    var prim = this._cgl.gl.TRIANGLES;
    if (this._glPrimitive !== undefined) prim = this._glPrimitive;
    if (shader.glPrimitive !== null) prim = shader.glPrimitive;


    if (this.hasFeedbacks())
    {
        this.drawFeedbacks(shader, prim);
    }
    else if (this._bufVerticesIndizes.numItems === 0)
    {
        if (this._numInstances === 0) this._cgl.gl.drawArrays(prim, this._bufVertexAttrib.startItem, this._bufVertexAttrib.numItems - this._bufVertexAttrib.startItem);
        else this._cgl.gl.drawArraysInstanced(prim, this._bufVertexAttrib.startItem, this._bufVertexAttrib.numItems, this._numInstances);
    }
    else
    if (this._numInstances === 0) this._cgl.gl.drawElements(prim, this._bufVerticesIndizes.numItems, this._cgl.gl.UNSIGNED_SHORT, 0);
    else this._cgl.gl.drawElementsInstanced(prim, this._bufVerticesIndizes.numItems, this._cgl.gl.UNSIGNED_SHORT, 0, this._numInstances);
};

Mesh.prototype.setNumInstances = function (n)
{
    this._numInstances = n;
    if (n > 0)
    {
        var indexArr = new Float32Array(n);
        for (var i = 0; i < n; i++) indexArr[i] = i;
        this.setAttribute("instanceIndex", indexArr, 1, { instanced: true });
    }
};

Mesh.prototype._disposeAttributes = function ()
{
    if (!this._attributes) return;

    for (var i = 0; i < this._attributes.length; i++)
    {
        if (this._attributes[i].buffer)
        {
            this._cgl.gl.deleteBuffer(this._attributes[i].buffer);
            this._attributes[i].buffer = null;
        }
    }
    this._attributes.length = 0;
};

Mesh.prototype.dispose = function ()
{
    if (this._bufVertexAttrib && this._bufVertexAttrib.buffer) this._cgl.gl.deleteBuffer(this._bufVertexAttrib.buffer);
    if (this._bufVerticesIndizes) this._cgl.gl.deleteBuffer(this._bufVerticesIndizes);

    this._disposeAttributes();
};

extendMeshWithFeedback(Mesh);

export { Mesh, MESH };

import { ShaderLibMods } from "./cgl_shader_lib";
import { now } from "../timer";
import { simpleId, generateUUID } from "../utils";
import { MESH } from "./cgl_mesh";
// import { CGL } from "./index";
import { profileData } from "./cgl_profiledata";
import{ CONSTANTS } from "./constants";

// ---------------------------------------------------------------------------
export const SHADER_VARS = {
profileShaderBinds: 0,
// profileUniformCount: 0,
profileShaderCompiles: 0,
profileVideosPlaying: 0,
profileMVPMatrixCount: 0,

// default attributes
SHADERVAR_VERTEX_POSITION: 'vPosition',
SHADERVAR_VERTEX_NUMBER: 'attrVertIndex',
SHADERVAR_VERTEX_TEXCOORD: 'attrTexCoord',
SHADERVAR_INSTANCE_MMATRIX: 'instMat',

// default uniforms
SHADERVAR_UNI_PROJMAT: "projMatrix",
SHADERVAR_UNI_VIEWMAT: "viewMatrix",
SHADERVAR_UNI_MODELMAT: "modelMatrix",
SHADERVAR_UNI_NORMALMAT: "normalMatrix",
SHADERVAR_UNI_INVVIEWMAT: "inverseViewMatrix",
SHADERVAR_UNI_VIEWPOS: "camPos",
}
// ---------------------------------------------------------------------------

/**
 * @class
 * @external CGL
 * @namespace Shader
 * @hideconstructor
 * @example
 * var shader=new CGL.Shader(cgl,'MinimalMaterial');
 * shader.setSource(attachments.shader_vert,attachments.shader_frag);
 */
const Shader = function(_cgl, _name) {
    if (!_cgl) throw "shader constructed without cgl";

    this._cgl = _cgl;
    this._name = _name || 'unknown';
    this.glslVersion = 0;
    if(_cgl.glVersion>1)this.glslVersion=300;

    this.id=simpleId();
    this._program = null;
    this._uniforms = [];
    this._drawBuffers=[true];
    this._defines = [];
    this._needsRecompile = true;

    this._projMatrixUniform = null;
    this._mvMatrixUniform = null;
    this._mMatrixUniform = null;
    this._vMatrixUniform = null;
    this._camPosUniform = null;
    this._normalMatrixUniform = null;
    this._inverseViewMatrixUniform = null;

    this._attrVertexPos = -1;
    this.precision = _cgl.patch.config.glslPrecision||'highp';

    this._pMatrixState =-1;
    this._vMatrixState =-1;

    this._modGroupCount = 0;
    this._feedBackNames = [];
    this._attributes = [];

    this.glPrimitive = null;
    this.offScreenPass = false;
    this._extensions = [];
    this.srcVert = this.getDefaultVertexShader();
    this.srcFrag = this.getDefaultFragmentShader();
    this.lastCompile = 0;

    this._moduleNames = [];
    this._modules = [];
    this._moduleNumId = 0;

    this._libs=[];

    this._tempNormalMatrix=mat4.create();
    this._tempCamPosMatrix = mat4.create();
    this._tempInverseViewMatrix = mat4.create();

    this.setModules(['MODULE_VERTEX_POSITION','MODULE_COLOR','MODULE_BEGIN_FRAG']);
};

Shader.prototype.getCgl = function() {
    return this._cgl;
};

Shader.prototype.getName=function(){
    return this._name;
}

Shader.prototype.enableExtension = function(name) {
    this.setWhyCompile("enable extension "+name);
    this._needsRecompile = true;
    this._extensions.push(name);
};

Shader.prototype.getAttrVertexPos = function() {
    return this._attrVertexPos;
};

Shader.prototype.hasTextureUniforms = function() {
    for (var i = 0; i < this._uniforms.length; i++)
        if (this._uniforms[i].getType() == 't') return true;
    return false;
};

Shader.prototype.setWhyCompile=function(why)
{
    // console.log('recompile because '+why);
};

Shader.prototype.setSource = function(srcVert, srcFrag) {
    this.srcVert = srcVert;
    this.srcFrag = srcFrag;
    this.setWhyCompile("Source changed");
    this._needsRecompile = true;
};


Shader.prototype._addLibs=function(src)
{
    for(var id in ShaderLibMods)
    {
        if(src.indexOf(id)>-1)
        {
            var lib=new ShaderLibMods[id]();
            src = src.replace('{{'+id+'}}', lib.srcHeadFrag);
            this._libs.push(lib);
            if(lib.initUniforms)lib.initUniforms(this);
        }
    }

    return src;
}



Shader.prototype.compile = function() {
    profileData.profileShaderCompiles++;
    profileData.profileShaderCompileName = this._name;

    var extensionString = '';
    if (this._extensions)
        for (i = 0; i < this._extensions.length; i++)
            extensionString += '#extension '+this._extensions[i]+' : enable'.endl();

    var definesStr = '';
    var i = 0;
    for (i = 0; i < this._defines.length; i++)
        definesStr += '#define ' + this._defines[i][0] + ' ' + this._defines[i][1] + ''.endl();

    if(this._uniforms)
        for (i = 0; i < this._uniforms.length; i++)
            this._uniforms[i].resetLoc();

    if (this.hasTextureUniforms()) definesStr += '#define HAS_TEXTURES'.endl();

    var vs = '';
    var fs = '';

    if (this.glslVersion == 300)
    {
        var drawBufferStr = '';
        var count=0;

        if(this.srcFrag.indexOf("outColor0")>-1)this._drawBuffers[0]=true;
        if(this.srcFrag.indexOf("outColor1")>-1)this._drawBuffers[1]=true;
        if(this.srcFrag.indexOf("outColor2")>-1)this._drawBuffers[2]=true;
        if(this.srcFrag.indexOf("outColor3")>-1)this._drawBuffers[3]=true;

        if(this._drawBuffers.length==1)
        {
            // drawBufferStr+='#define gl_FragColor outColor'+i+''.endl();
            drawBufferStr='out vec4 outColor;'.endl();
            drawBufferStr+='#define gl_FragColor outColor'.endl();
        }
        else
        {
            var count=0;
            drawBufferStr+='vec4 outColor;'.endl();


            for(var i=0;i<this._drawBuffers.length;i++)
            {
                // if(this._drawBuffers[i])
                {
                    if(count==0)
                    {
                        drawBufferStr+='#define gl_FragColor outColor'+i+''.endl();
                        // drawBufferStr+='#define outColor outColor'+i+''.endl();
                    }
                    drawBufferStr+='layout(location = '+i+') out vec4 outColor'+i+';'.endl();
                }
                count++;
            }

        }


        vs = '#version 300 es'
            .endl() + '// '
            .endl() + '// vertex shader '+this._name
            .endl() + '// '
            .endl() + 'precision ' + this.precision+' float;'
            .endl() + ''
            .endl() + '#define WEBGL2'
            .endl() + '#define texture2D texture'
            .endl() + '#define UNI uniform'
            .endl() + '#define IN in'
            .endl() + '#define OUT out'
            .endl();

        fs = '#version 300 es'
            .endl() + '// '
            .endl() + '// fragment shader '+this._name
            .endl() + '// '
            .endl() + 'precision ' + this.precision+' float;'
            .endl() + ''
            .endl() + '#define WEBGL2'
            .endl() + '#define texture2D texture'
            .endl() + '#define IN in'
            .endl() + '#define UNI uniform'
            .endl() + drawBufferStr

            .endl();

    } else {
        fs = ''
            .endl() + '// '
            .endl() + '// fragment shader '+this._name
            .endl() + '// '
            .endl() + '#define WEBGL1'
            .endl() + '#define texture texture2D'
            .endl() + '#define outColor gl_FragColor'
            .endl() + '#define IN varying'
            .endl() + '#define UNI uniform'
            .endl();

        vs = ''
            .endl() + '// '
            .endl() + '// vertex shader '+this._name
            .endl() + '// '
            .endl() + '#define WEBGL1'
            .endl() + '#define texture texture2D'
            .endl() + '#define OUT varying'
            .endl() + '#define IN attribute'
            .endl() + '#define UNI uniform'
            .endl();
        }

    if (fs.indexOf("precision") == -1) fs = 'precision ' + this.precision+' float;'.endl() + fs;
    if (vs.indexOf("precision") == -1) vs = 'precision ' + this.precision+' float;'.endl() + vs;

    if( /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) )
    {
        fs+='#define MOBILE'.endl();
        vs+='#define MOBILE'.endl();
    }

    vs = extensionString + vs + definesStr + this.srcVert;
    fs = extensionString + fs + definesStr + this.srcFrag;

    var srcHeadVert = '';
    var srcHeadFrag = '';

    this._modules.sort(function(a, b) {
        return a.group - b.group;
    });

    this._modules.sort(function(a, b) {
        return a.priority||0 - b.priority||0;
    });

    var addedAttributes=false;

    for (i = 0; i < this._moduleNames.length; i++) {
        var srcVert = '';
        var srcFrag = '';

        for (var j = 0; j < this._modules.length; j++) {
            if (this._modules[j].name == this._moduleNames[i]) {

                srcHeadVert+='\n//---- MOD: '+this._modules[j].group+': '+j+' - '+this._modules[j].title+' ------\n';
                srcHeadFrag+='\n//---- MOD: '+this._modules[j].group+': '+j+' - '+this._modules[j].title+' ------\n';

                srcVert+='\n\n//---- MOD: '+this._modules[j].title+' ------\n';
                srcFrag+='\n\n//---- MOD: '+this._modules[j].title+' ------\n';

                if(!addedAttributes)
                {
                    addedAttributes=true;

                    for(var k=0;k<this._attributes.length;k++)
                    {
                        if(this._attributes[k].name && this._attributes[k].type)
                        {
                            srcHeadVert+=''
                                .endl()+'#ifndef ATTRIB_'+this._attributes[k].name
                                .endl()+'  #define ATTRIB_'+this._attributes[k].name
                                .endl()+'  IN '+this._attributes[k].type+' '+this._attributes[k].name+';'
                                .endl()+'#endif';

                            if(this._attributes[k].nameFrag)
                            {
                                srcHeadVert+=''
                                    .endl()+'#ifndef ATTRIB_'+this._attributes[k].nameFrag
                                    .endl()+'  #define ATTRIB_'+this._attributes[k].nameFrag
                                    .endl()+'  OUT '+this._attributes[k].type+' '+this._attributes[k].nameFrag+';'
                                    .endl()+'#endif';

                                srcVert+=''
                                    .endl()+this._attributes[k].nameFrag+'='+this._attributes[k].name+';';
                            }

                            srcHeadFrag+=''
                                .endl()+'#ifndef ATTRIB_'+this._attributes[k].nameFrag
                                .endl()+'  #define ATTRIB_'+this._attributes[k].nameFrag
                                .endl()+'  IN '+this._attributes[k].type+' '+this._attributes[k].nameFrag+';'
                                .endl()+'#endif';
                        }
                    }
                }

                srcHeadVert += this._modules[j].srcHeadVert || '';
                srcHeadFrag += this._modules[j].srcHeadFrag || '';
                srcVert += this._modules[j].srcBodyVert || '';
                srcFrag += this._modules[j].srcBodyFrag || '';

                srcHeadVert+='\n//---- end mod ------\n';
                srcHeadFrag+='\n//---- end mod ------\n';

                srcVert+='\n//---- end mod ------\n';
                srcFrag+='\n//---- end mod ------\n';

                srcVert = srcVert.replace(/{{mod}}/g, this._modules[j].prefix);
                srcFrag = srcFrag.replace(/{{mod}}/g, this._modules[j].prefix);
                srcHeadVert = srcHeadVert.replace(/{{mod}}/g, this._modules[j].prefix);
                srcHeadFrag = srcHeadFrag.replace(/{{mod}}/g, this._modules[j].prefix);

                srcVert = srcVert.replace(/MOD_/g, this._modules[j].prefix);
                srcFrag = srcFrag.replace(/MOD_/g, this._modules[j].prefix);
                srcHeadVert = srcHeadVert.replace(/MOD_/g, this._modules[j].prefix);
                srcHeadFrag = srcHeadFrag.replace(/MOD_/g, this._modules[j].prefix);
            }
        }


        vs = vs.replace('{{' + this._moduleNames[i] + '}}', srcVert);
        fs = fs.replace('{{' + this._moduleNames[i] + '}}', srcFrag);
    }
    vs = vs.replace('{{MODULES_HEAD}}', srcHeadVert);
    fs = fs.replace('{{MODULES_HEAD}}', srcHeadFrag);

    vs=this._addLibs(vs);
    fs=this._addLibs(fs);

    if (!this._program) {
        this._program = this._createProgram(vs, fs);
    } else {
        // this.vshader=createShader(vs, gl.VERTEX_SHADER, this.vshader );
        // this.fshader=createShader(fs, gl.FRAGMENT_SHADER, this.fshader );
        // linkProgram(program);
        this._program = this._createProgram(vs, fs);

        this._projMatrixUniform = null;

        for (i = 0; i < this._uniforms.length; i++) this._uniforms[i].resetLoc();
    }

    this.finalShaderFrag = fs;
    this.finalShaderVert = vs;

    MESH.lastMesh = null;
    MESH.lastShader = null;

    this._needsRecompile = false;
    this.lastCompile = now();
};

Shader.prototype.bind = function()
{
    var i = 0;
    MESH.lastShader = this;

    if (!this._program || this._needsRecompile) this.compile();

    if (!this._projMatrixUniform) {
        this._attrVertexPos = this._cgl.glGetAttribLocation(this._program, CONSTANTS.SHADER.SHADERVAR_VERTEX_POSITION);
        this._projMatrixUniform = this._cgl.gl.getUniformLocation(this._program, CONSTANTS.SHADER.SHADERVAR_UNI_PROJMAT);
        this._mvMatrixUniform = this._cgl.gl.getUniformLocation(this._program, "mvMatrix");
        this._vMatrixUniform = this._cgl.gl.getUniformLocation(this._program, CONSTANTS.SHADER.SHADERVAR_UNI_VIEWMAT);
        this._mMatrixUniform = this._cgl.gl.getUniformLocation(this._program, CONSTANTS.SHADER.SHADERVAR_UNI_MODELMAT);
        this._camPosUniform = this._cgl.gl.getUniformLocation(this._program, CONSTANTS.SHADER.SHADERVAR_UNI_VIEWPOS);
        this._normalMatrixUniform = this._cgl.gl.getUniformLocation(this._program, CONSTANTS.SHADER.SHADERVAR_UNI_NORMALMAT);
        this._inverseViewMatrixUniform = this._cgl.gl.getUniformLocation(this._program, CONSTANTS.SHADER.SHADERVAR_UNI_INVVIEWMAT);
        for (i = 0; i < this._uniforms.length; i++) this._uniforms[i].needsUpdate = true;
    }

    if (this._cgl.currentProgram != this._program) {
        profileData.profileShaderBinds++;
        this._cgl.gl.useProgram(this._program);
        this._cgl.currentProgram = this._program;
    }

    for (i = 0; i < this._uniforms.length; i++)
        if (this._uniforms[i].needsUpdate) this._uniforms[i].updateValue();

    if(this._pMatrixState!=this._cgl.getProjectionMatrixStateCount())
    {
        this._pMatrixState=this._cgl.getProjectionMatrixStateCount();
        this._cgl.gl.uniformMatrix4fv(this._projMatrixUniform, false, this._cgl.pMatrix);
        profileData.profileMVPMatrixCount++;
    }

    if (this._vMatrixUniform)
    {
        if(this._vMatrixState!=this._cgl.getViewMatrixStateCount())
        {
            this._cgl.gl.uniformMatrix4fv(this._vMatrixUniform, false, this._cgl.vMatrix);
            profileData.profileMVPMatrixCount++;
            this._vMatrixState=this._cgl.getViewMatrixStateCount();

            if (this._inverseViewMatrixUniform)
            {
                mat4.invert(this._tempInverseViewMatrix, this._cgl.vMatrix);
                this._cgl.gl.uniformMatrix4fv(this._inverseViewMatrixUniform, false, this._tempInverseViewMatrix);
                profileData.profileMVPMatrixCount++;
            }
        }
        this._cgl.gl.uniformMatrix4fv(this._mMatrixUniform, false, this._cgl.mMatrix);
        profileData.profileMVPMatrixCount++;

        if(this._camPosUniform)
        {
            mat4.invert(this._tempCamPosMatrix, this._cgl.vMatrix);
            this._cgl.gl.uniform3f(this._camPosUniform, this._tempCamPosMatrix[12], this._tempCamPosMatrix[13], this._tempCamPosMatrix[14]);
            profileData.profileMVPMatrixCount++;
        }
    }
    else
    {
        // mvmatrix deprecated....
        var tempmv=mat4.create();

        mat4.mul(tempmv, this._cgl.vMatrix, this._cgl.mMatrix);
        this._cgl.gl.uniformMatrix4fv(this._mvMatrixUniform, false, tempmv);
        profileData.profileMVPMatrixCount++;
    }

    if (this._normalMatrixUniform)
    {
        mat4.mul(this._tempNormalMatrix, this._cgl.vMatrix, this._cgl.mMatrix);

        // this._tempNormalMatrix[3]=0;
        // this._tempNormalMatrix[6]=0;
        // this._tempNormalMatrix[9]=0;
        // this._tempNormalMatrix[12]=0;
        // this._tempNormalMatrix[13]=0;
        // this._tempNormalMatrix[14]=0;
        // this._tempNormalMatrix[15]=1;

        mat4.invert(this._tempNormalMatrix, this._tempNormalMatrix);
        mat4.transpose(this._tempNormalMatrix, this._tempNormalMatrix);

        this._cgl.gl.uniformMatrix4fv(this._normalMatrixUniform, false, this._tempNormalMatrix);
        profileData.profileMVPMatrixCount++;
    }

    for(var i=0;i<this._libs.length;i++)
    {
        if(this._libs[i].onBind)this._libs[i].onBind.bind(this._libs[i])(this._cgl,this);
    }
};


/**
 * easily enable/disable a define without a value
 * @function toggleDefine
 * @memberof Shader
 * @instance
 * @param {name} name
 */
Shader.prototype.toggleDefine = function(name, enabled)
{
    if(enabled) this.define(name);
        else this.removeDefine(name);
};

/**
 * add a define to a shader, e.g.  #define DO_THIS_THAT 1
 * @function define
 * @memberof Shader
 * @instance
 * @param {String} name
 * @param {Any} value (can be empty)

 */
Shader.prototype.define = function(name, value)
{
    if (!value) value = '';
    for (var i = 0; i < this._defines.length; i++)
    {
        if (this._defines[i][0] == name && this._defines[i][1] == value) return;
        if (this._defines[i][0] == name) {
            this._defines[i][1] = value;
            this._needsRecompile = true;
            return;
        }
    }

    this._defines.push([name, value]);
    this._needsRecompile = true;
    this.setWhyCompile("define "+name+" "+value);
};

Shader.prototype.getDefines=function()
{
    return this._defines;
};

Shader.prototype.getDefine = function(name) {
    for (var i = 0; i < this._defines.length; i++)
        if (this._defines[i][0] == name) return this._defines[i][1];
    return null;
};

Shader.prototype.hasDefine = function(name) {
    for (var i = 0; i < this._defines.length; i++)
        if (this._defines[i][0] == name) return true;
};

/**
 * remove a define from a shader
 * @param {name} name
 * @function removeDefine
 * @memberof Shader
 * @instance
 */
Shader.prototype.removeDefine = function(name) {
    for (var i = 0; i < this._defines.length; i++) {
        if (this._defines[i][0] == name) {
            this._defines.splice(i, 1);
            this._needsRecompile = true;
            return;
        }
    }
};







/**
 * remove a module from shader
 * @function removeModule
 * @memberof Shader
 * @instance
 * @param {shaderModule} module the module to be removed
 */
Shader.prototype.removeModule = function(mod)
{
    for (var i = 0; i < this._modules.length; i++)
    {
        if(mod && mod.id)
        {
            if (this._modules[i].id == mod.id || !this._modules[i])
            {
                var found=true;
                while(found)
                {
                    found=false;
                    for(var j=0;j<this._uniforms.length;j++)
                    {
                        if(this._uniforms[j].getName().indexOf(mod.prefix)==0)
                        {
                            this._uniforms.splice(j, 1);
                            found=true;
                            continue;
                        }
                    }
                }

                this._needsRecompile = true;
                this.setWhyCompile("remove module "+mod.title);
                this._modules.splice(i, 1);
                break;
            }
        }
    }
};


Shader.prototype.getNumModules = function() {
    return this._modules.length;
};


Shader.prototype.getCurrentModules=function(){return this._modules;};


/**
 * add a module
 * @function addModule
 * @memberof Shader
 * @instance
 * @param {shaderModule} module the module to be added
 * @param {shaderModule} [sibling] sibling module, new module will share the same group
 */
Shader.prototype.addModule = function(mod, sibling) {
    if(!mod.id) mod.id = generateUUID();
    if(!mod.numId) mod.numId = this._moduleNumId;
    if(!mod.num)mod.num = this._modules.length;

    if(sibling) mod.group = sibling.group;
        else mod.group = this._modGroupCount++;

    mod.prefix = 'mod' + mod.group;

    this._modules.push(mod);
    this._needsRecompile = true;
    this.setWhyCompile("add module "+mod.title);
    this._moduleNumId++;

    return mod;
};

Shader.prototype.setModules = function(names) {
    this._moduleNames = names;
};

Shader.prototype.dispose=function()
{
    this._cgl.gl.deleteProgram(this._program);
};




















Shader.prototype.setDrawBuffers = function(arr) {
    this._drawBuffers=arr;
    this._needsRecompile=true;
};

Shader.prototype.getUniforms = function() {
    return this._uniforms;
};

Shader.prototype.getUniform = function(name) {
    for (var i = 0; i < this._uniforms.length; i++)
        if (this._uniforms[i].getName() == name)
            return this._uniforms[i];
    return null;
};

Shader.prototype.removeUniform = function(name) {
    for (var i = 0; i < this._uniforms.length; i++) {
        if (this._uniforms[i].getName() == name) {
            this._uniforms.splice(i, 1);
        }
    }
    this._needsRecompile = true;
    this.setWhyCompile("remove uniform "+name);
};

/**
 * add a uniform to the shader
 * @param {Uniform} uniform
 * @memberof Shader
 * @instance
 * @function
 */
Shader.prototype.addUniform = function(uni) {
    this._uniforms.push(uni);
    this.setWhyCompile("add uniform "+name);
    this._needsRecompile = true;
};

Shader.prototype._createProgram = function(vstr, fstr) {
    var program = this._cgl.gl.createProgram();
    this.vshader = Shader.createShader(this._cgl, vstr, this._cgl.gl.VERTEX_SHADER, this);
    this.fshader = Shader.createShader(this._cgl, fstr, this._cgl.gl.FRAGMENT_SHADER, this);

    this._cgl.gl.attachShader(program, this.vshader);
    this._cgl.gl.attachShader(program, this.fshader);

    this._linkProgram(program);
    return program;
};

Shader.prototype.hasErrors = function()
{
    return this._hasErrors;
}

Shader.prototype._linkProgram = function(program)
{
    if (this._feedBackNames.length > 0) {
        this._cgl.gl.transformFeedbackVaryings(program, this._feedBackNames, this._cgl.gl.SEPARATE_ATTRIBS);
        // INTERLEAVED_ATTRIBS
        //SEPARATE_ATTRIBS
    }

    this._cgl.gl.linkProgram(program);
    this._cgl.gl.validateProgram(program);

    // var infoLog = this._cgl.gl.getProgramInfoLog(program);
    // if (infoLog) {
    //     // console.log(name+' link programinfo: ',this._cgl.gl.getProgramInfoLog(program));
    // }

    if (!this._cgl.gl.getProgramParameter(program, this._cgl.gl.LINK_STATUS)) {


        // todo print shaderinfolog!!!!

        console.warn(this._cgl.gl.getShaderInfoLog(this.fshader));
        console.warn(this._cgl.gl.getShaderInfoLog(this.vshader));

        console.error(name + " shader linking fail...");
        console.log('srcFrag',this.srcFrag);
        console.log('srcVert',this.srcVert);
        console.log(name + ' programinfo: ', this._cgl.gl.getProgramInfoLog(program));

        console.log('--------------------------------------');
        console.log(this);
        console.log('--------------------------------------');

        name = "errorshader";
        this.setSource(Shader.getDefaultVertexShader(), Shader.getErrorFragmentShader());
    }
    else
    {

    }

    // var error = this._cgl.gl.getError();
    // if (error == this._cgl.gl.NO_ERROR )
    // console.log('no error: ',error);
    // else
    //   console.log('get error: ',error);
    // if(this._feedBackNames.length>0)
    //    this._cgl.gl.transformFeedbackVaryings( program, [], this._cgl.gl.SEPARATE_ATTRIBS );

};

Shader.prototype.getProgram = function() {
    return this._program;
};

Shader.prototype.setFeedbackNames = function(names) {
    this._needsRecompile = true;
    this._feedBackNames = names;
};

Shader.prototype.getDefaultVertexShader = Shader.getDefaultVertexShader = function() {
    return ''
        .endl() + '{{MODULES_HEAD}}'
        .endl() + 'IN vec3 vPosition;'
        .endl() + 'IN vec2 attrTexCoord;'
        .endl() + 'IN vec3 attrVertNormal;'
        .endl() + 'IN float attrVertIndex;'

        .endl() + 'OUT vec2 texCoord;'
        .endl() + 'OUT vec3 norm;'
        .endl() + 'UNI mat4 projMatrix;'
        .endl() + 'UNI mat4 viewMatrix;'
        .endl() + 'UNI mat4 modelMatrix;'

        .endl() + 'void main()'
        .endl() + '{'
        .endl() + '   texCoord=attrTexCoord;'
        .endl() + '   norm=attrVertNormal;'
        .endl() + '   vec4 pos=vec4(vPosition,  1.0);'
        .endl() + '   mat4 mMatrix=modelMatrix;'
        .endl() + '   {{MODULE_VERTEX_POSITION}}'
        .endl() + '   gl_Position = projMatrix * (viewMatrix*mMatrix) * pos;'
        .endl() + '}';
};

Shader.prototype.getDefaultFragmentShader = Shader.getDefaultFragmentShader = function(r,g,b)
{
    if(r==undefined)
    {
        r=0.5;
        g=0.5;
        b=0.5;
    }
    return ''
        .endl()+'IN vec2 texCoord;'
        .endl()+'{{MODULES_HEAD}}'
        .endl()+'void main()'
        .endl()+'{'
        .endl()+'    vec4 col=vec4('+r+','+g+','+b+',1.0);'
        .endl()+'    {{MODULE_COLOR}}'
        .endl()+'    outColor = col;'
        .endl()+'}';
};

/**
  * adds attribute definition to shader header without colliding with other shader modules...
 * when attrFrag is defined, vertex shader will output this attribute to the fragment shader
 * @function
 * @memberof Shader
 * @instance
 * @param {Object} attribObject {type:x,name:x,[nameFrag:x]}
 * @return {Object}
 */
Shader.prototype.addAttribute = function(attr) {
    for(var i=0;i<this._attributes.length;i++)
    {
        if(this._attributes[i].name==attr.name && this._attributes[i].nameFrag==attr.nameFrag)return;
    }
    this._attributes.push(attr);
    this._needsRecompile = true;
};

// --------------------------

Shader.getErrorFragmentShader = function() {
    return ''
        .endl() + 'void main()'
        .endl() + '{'
        .endl() + '   float g=mod((gl_FragCoord.y+gl_FragCoord.x),50.0)/50.0;'
        .endl() + '   g= step(0.1,g);'
        .endl() + '   outColor = vec4( g+0.5, 0.0, 0.0, 1.0);'
        .endl() + '}';
};

Shader.createShader = function(cgl, str, type, cglShader) {
    function getBadLines(infoLog) {
        var basLines = [];
        var lines = infoLog.split('\n');
        for (var i in lines) {
            var divide = lines[i].split(':');
            if (parseInt(divide[2], 10)) basLines.push(parseInt(divide[2], 10));
        }
        return basLines;
    }

    var shader = cgl.gl.createShader(type);
    cgl.gl.shaderSource(shader, str);
    cgl.gl.compileShader(shader);

    if (!cgl.gl.getShaderParameter(shader, cgl.gl.COMPILE_STATUS)) {
        console.log('compile status: ');

        if (type == cgl.gl.VERTEX_SHADER) console.log('VERTEX_SHADER');
        if (type == cgl.gl.FRAGMENT_SHADER) console.log('FRAGMENT_SHADER');

        console.warn(cgl.gl.getShaderInfoLog(shader));

        var infoLog = cgl.gl.getShaderInfoLog(shader);
        var badLines = getBadLines(infoLog);
        var htmlWarning = '<div class="shaderErrorCode">';
        var lines = str.match(/^.*((\r\n|\n|\r)|$)/gm);

        for (var i in lines) {
            var j = parseInt(i, 10) + 1;
            var line = j + ': ' + lines[i];
            console.log(line);

            var isBadLine = false;
            for (var bj in badLines)
                if (badLines[bj] == j) isBadLine = true;

            if (isBadLine) htmlWarning += '<span class="error">';
            htmlWarning += line;
            if (isBadLine) htmlWarning += '</span>';
        }

        console.warn(infoLog);

        infoLog = infoLog.replace(/\n/g, '<br/>');

        htmlWarning = infoLog + '<br/>' + htmlWarning + '<br/><br/>';

        cgl.patch.emitEvent("criticalError",'Shader error ' + name, htmlWarning);
        if(cgl.patch.isEditorMode())console.log('Shader error ' + name, htmlWarning);

        htmlWarning += '</div>';

        name = "errorshader";
        cglShader.setSource(Shader.getDefaultVertexShader(), Shader.getErrorFragmentShader());
    } else {
        // console.log(name+' shader compiled...');
    }
    return shader;
};

export { Shader };

// import { vec3, mat4 } from "gl-matrix";
import { CONSTANTS } from "./constants";
import { Shader } from "./cgl_shader";
import { MatrixStack } from "./cgl_matrixstack";

/**
 * cables gl context/state manager
 * @external CGL
 * @namespace Context
 * @class
 * @hideconstructor
 */
const Context = function (_patch)
{
    var self = this;
    var viewPort = [0, 0, 0, 0];
    this.glVersion = 0;
    this.glUseHalfFloatTex = false;
    this.clearCanvasTransparent = true;
    this.clearCanvasDepth = true;
    this.patch = _patch;

    this.temporaryTexture = null;
    this.frameStore = {};
    this.gl = null;

    /**
     * Current projection matrix
     * @member pMatrix
     * @memberof Context
     * @instance
     * @type {mat4}
     */
    this.pMatrix = mat4.create();
    /**
     * Current model matrix
     * @member pMatrix
     * @memberof Context
     * @instance
     * @type {mat4}
     */
    this.mMatrix = mat4.create();
    /**
     * Current view matrix
     * @member vMatrix
     * @memberof Context
     * @instance
     * @type {mat4}
     */
    this.vMatrix = mat4.create();
    this._textureslots = [];

    this._pMatrixStack = new MatrixStack();
    this._mMatrixStack = new MatrixStack();
    this._vMatrixStack = new MatrixStack();
    this._glFrameBufferStack = [];
    this._frameBufferStack = [];
    this._shaderStack = [];

    Object.defineProperty(this, "mvMatrix", {
        get()
        {
            return this.mMatrix;
        },
        set(m)
        {
            this.mMatrix = m;
        },
    }); // todo: deprecated

    this.canvas = null;
    this.pixelDensity = 1;
    mat4.identity(this.mMatrix);
    mat4.identity(this.vMatrix);

    var simpleShader = new Shader(this, "simpleshader");

    simpleShader.setModules(["MODULE_VERTEX_POSITION", "MODULE_COLOR", "MODULE_BEGIN_FRAG"]);
    simpleShader.setSource(Shader.getDefaultVertexShader(), Shader.getDefaultFragmentShader());

    var currentShader = simpleShader;
    var aborted = false;
    var cbResize = [];

    this.addEventListener = function (event, cb)
    {
        if (event == "resize") cbResize.push(cb);
    };

    this.removeEventListener = function (event, cb)
    {
        if (event == "resize")
        {
            for (var i in cbResize)
            {
                if (cbResize[i] == cb)
                {
                    cbResize.splice(i, 1);
                    return;
                }
            }
        }
    };

    this.exitError = function (msgId, msg)
    {
        this.patch.exitError(msgId, msg);
        this.aborted = true;
    };

    this.setCanvas = function (canv)
    {
        // CGL.TextureEffectMesh = CGL.TextureEffectMesh || null;

        if (typeof canv === "string") this.canvas = document.getElementById(canv);
        else this.canvas = canv;

        if (!this.patch.config.canvas) this.patch.config.canvas = {};

        if (!this.patch.config.canvas.hasOwnProperty("preserveDrawingBuffer")) this.patch.config.canvas.preserveDrawingBuffer = false;
        if (!this.patch.config.canvas.hasOwnProperty("premultipliedAlpha")) this.patch.config.canvas.premultipliedAlpha = false;
        if (!this.patch.config.canvas.hasOwnProperty("alpha")) this.patch.config.canvas.alpha = false;

        if (this.patch.config.hasOwnProperty("clearCanvasColor")) this.clearCanvasTransparent = this.patch.config.clearCanvasColor;
        if (this.patch.config.hasOwnProperty("clearCanvasDepth")) this.clearCanvasDepth = this.patch.config.clearCanvasDepth;

        this.gl = this.canvas.getContext("webgl2", this.patch.config.canvas);
        if (this.gl)
        {
            this.glVersion = 2;
        }
        else
        {
            this.gl = this.canvas.getContext("webgl", this.patch.config.canvas) || this.canvas.getContext("experimental-webgl", this.patch.config.canvas);
            this.glVersion = 1;

            // safari
            if (/^((?!chrome|android).)*safari/i.test(navigator.userAgent))
            {
                this.glUseHalfFloatTex = true;
            }

            // ios
            if (/iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream)
            {
                if (!this.patch.config.canvas.hasOwnProperty("powerPreference")) this.patch.config.canvas.powerPreference = "high-performance";
            }
        }

        if (!this.gl)
        {
            this.exitError("NO_WEBGL", "sorry, could not initialize WebGL. Please check if your Browser supports WebGL.");
            return;
        }
        var derivativeExt = this.gl.getExtension("GL_OES_standard_derivatives");
        var instancingExt = this.gl.getExtension("ANGLE_instanced_arrays") || this.gl;

        if (instancingExt.vertexAttribDivisorANGLE)
        {
            this.gl.vertexAttribDivisor = instancingExt.vertexAttribDivisorANGLE.bind(instancingExt);
            this.gl.drawElementsInstanced = instancingExt.drawElementsInstancedANGLE.bind(instancingExt);
        }
        self.updateSize();
    };

    this.updateSize = function ()
    {
        this.canvas.width = this.canvasWidth = this.canvas.clientWidth * this.pixelDensity;
        this.canvas.height = this.canvasHeight = this.canvas.clientHeight * this.pixelDensity;
    };

    this.canvasWidth = -1;
    this.canvasHeight = -1;
    this.canvasScale = 1;
    var oldCanvasWidth = -1;
    var oldCanvasHeight = -1;

    /**
     * @function getViewPort
     * @memberof Context
     * @instance
     * @description get current gl viewport
     * @returns {Array} array [x,y,w,h]
     */
    this.getViewPort = function ()
    {
        return viewPort;
    };

    this.resetViewPort = function ()
    {
        this.gl.viewport(viewPort[0], viewPort[1], viewPort[2], viewPort[3]);
    };

    /**
     * @function setViewPort
     * @memberof Context
     * @instance
     * @description set current gl viewport
     * @param {Number} x
     * @param {Number} y
     * @param {Number} w
     * @param {Number} h
     */
    this.setViewPort = function (x, y, w, h)
    {
        viewPort[0] = Math.round(x);
        viewPort[1] = Math.round(y);
        viewPort[2] = Math.round(w);
        viewPort[3] = Math.round(h);
        this.gl.viewport(viewPort[0], viewPort[1], viewPort[2], viewPort[3]);
    };

    this.beginFrame = function ()
    {
        if (CABLES.UI)
        {
            gui.metaTexturePreviewer.render();
            if (CABLES.UI.patchPreviewer) CABLES.UI.patchPreviewer.render();
        }

        self.setShader(simpleShader);
    };

    this.screenShot = function (cb, doScreenshotClearAlpha)
    {
        if (doScreenshotClearAlpha)
        {
            this.gl.clearColor(1, 1, 1, 1);
            this.gl.colorMask(false, false, false, true);
            this.gl.clear(this.gl.COLOR_BUFFER_BIT);
            this.gl.colorMask(true, true, true, true);
        }

        if (this.canvas && this.canvas.toBlob)
        {
            this.canvas.toBlob((blob) =>
            {
                if (cb) cb(blob);
                else console.log("no screenshot callback...");
            });
        }
    };

    this.endFrame = function ()
    {
        if (CABLES.UI) CABLES.GL_MARKER.drawMarkerLayer(this);

        self.setPreviousShader();
        if (this._vMatrixStack.length() > 0) console.warn("view matrix stack length !=0 at end of rendering...");
        if (this._mMatrixStack.length() > 0) console.warn("mvmatrix stack length !=0 at end of rendering...");
        if (this._pMatrixStack.length() > 0) console.warn("pmatrix stack length !=0 at end of rendering...");
        if (this._glFrameBufferStack.length > 0) console.warn("glFrameBuffer stack length !=0 at end of rendering...");
        if (this._stackDepthTest.length > 0) console.warn("depthtest stack length !=0 at end of rendering...");
        if (this._stackDepthWrite.length > 0) console.warn("depthwrite stack length !=0 at end of rendering...");
        if (this._stackDepthFunc.length > 0) console.warn("depthfunc stack length !=0 at end of rendering...");
        if (this._stackBlend.length > 0) console.warn("blend stack length !=0 at end of rendering...");
        if (this._stackBlendMode.length > 0) console.warn("blendMode stack length !=0 at end of rendering...");
        if (this._shaderStack.length > 0) console.warn("this._shaderStack length !=0 at end of rendering...");

        if (oldCanvasWidth != self.canvasWidth || oldCanvasHeight != self.canvasHeight)
        {
            oldCanvasWidth = self.canvasWidth;
            oldCanvasHeight = self.canvasHeight;
            this.setSize(self.canvasWidth / this.pixelDensity, self.canvasHeight / this.pixelDensity);
            this.updateSize();

            for (var i = 0; i < cbResize.length; i++) cbResize[i]();
        }
    };

    // shader stack
    this.getShader = function ()
    {
        if (currentShader) if (!this.frameStore || ((this.frameStore.renderOffscreen === true) == currentShader.offScreenPass) === true) return currentShader;

        for (var i = this._shaderStack.length - 1; i >= 0; i--) if (this._shaderStack[i]) if (this.frameStore.renderOffscreen == this._shaderStack[i].offScreenPass) return this._shaderStack[i];
    };

    this.getDefaultShader = function ()
    {
        return simpleShader;
    };

    this.setShader = function (shader)
    {
        this._shaderStack.push(shader);
        currentShader = shader;
    };

    this.setPreviousShader = function ()
    {
        if (this._shaderStack.length === 0) throw "Invalid shader stack pop!";
        this._shaderStack.pop();
        currentShader = this._shaderStack[this._shaderStack.length - 1];
    };

    /**
     * push a framebuffer to the framebuffer stack
     * @function pushGlFrameBuffer
     * @memberof Context
     * @instance
     * @param {Object} framebuffer
     * @function
     */
    this.pushGlFrameBuffer = function (fb)
    {
        this._glFrameBufferStack.push(fb);
    };

    /**
     * pop framebuffer stack
     * @function popGlFrameBuffer
     * @memberof Context
     * @instance
     * @returns {Object} current framebuffer or null
     */
    this.popGlFrameBuffer = function ()
    {
        if (this._glFrameBufferStack.length == 0) return null;
        this._glFrameBufferStack.pop();
        return this._glFrameBufferStack[this._glFrameBufferStack.length - 1];
    };

    /**
     * get current framebuffer
     * @function getCurrentFrameBuffer
     * @memberof Context
     * @instance
     * @returns {Object} current framebuffer or null
     */
    this.getCurrentGlFrameBuffer = function ()
    {
        if (this._glFrameBufferStack.length === 0) return null;
        return this._glFrameBufferStack[this._glFrameBufferStack.length - 1];
    };

    /**
     * push a framebuffer to the framebuffer stack
     * @function pushGlFrameBuffer
     * @memberof Context
     * @instance
     * @param {Framebuffer} framebuffer
     */
    this.pushFrameBuffer = function (fb)
    {
        this._frameBufferStack.push(fb);
    };

    /**
     * pop framebuffer stack
     * @function popFrameBuffer
     * @memberof Context
     * @instance
     * @returns {Framebuffer} current framebuffer or null
     */
    this.popFrameBuffer = function ()
    {
        if (this._frameBufferStack.length == 0) return null;
        this._frameBufferStack.pop();
        return this._frameBufferStack[this._frameBufferStack.length - 1];
    };

    /**
     * get current framebuffer
     * @function getCurrentFrameBuffer
     * @memberof Context
     * @instance
     * @returns {Framebuffer} current framebuffer or null
     */
    this.getCurrentFrameBuffer = function ()
    {
        if (this._frameBufferStack.length === 0) return null;
        return this._frameBufferStack[this._frameBufferStack.length - 1];
    };

    var identView = vec3.create();
    vec3.set(identView, 0, 0, 2);
    var ident = vec3.create();
    vec3.set(ident, 0, 0, 0);

    this.renderStart = function (cgl, identTranslate, identTranslateView)
    {
        if (!identTranslate) identTranslate = ident;
        if (!identTranslateView) identTranslateView = identView;

        this.pushDepthTest(true);
        this.pushDepthWrite(true);
        this.pushDepthFunc(cgl.gl.LEQUAL);

        if (this.clearCanvasTransparent)
        {
            cgl.gl.clearColor(0, 0, 0, 0);
            cgl.gl.clear(cgl.gl.COLOR_BUFFER_BIT);
        }
        if (this.clearCanvasDepth) cgl.gl.clear(cgl.gl.DEPTH_BUFFER_BIT);

        cgl.setViewPort(0, 0, cgl.canvasWidth, cgl.canvasHeight);

        mat4.perspective(cgl.pMatrix, 45, cgl.canvasWidth / cgl.canvasHeight, 0.1, 1000.0);
        mat4.identity(cgl.mMatrix);
        mat4.identity(cgl.vMatrix);
        mat4.translate(cgl.mMatrix, cgl.mMatrix, identTranslate);
        mat4.translate(cgl.vMatrix, cgl.vMatrix, identTranslateView);

        cgl.pushPMatrix();
        cgl.pushModelMatrix();
        cgl.pushViewMatrix();

        cgl.pushBlendMode(CONSTANTS.BLEND_MODES.BLEND_NORMAL, false);

        for (var i = 0; i < this._textureslots.length; i++) this._textureslots[i] = null;

        cgl.beginFrame();
    };

    this.renderEnd = function (cgl, identTranslate)
    {
        cgl.popViewMatrix();
        cgl.popModelMatrix();
        cgl.popPMatrix();

        this.popDepthTest();
        this.popDepthWrite();
        this.popDepthFunc();
        this.popBlend();
        this.popBlendMode();

        cgl.endFrame();
    };

    this.getTexture = function (slot)
    {
        return this._textureslots[slot];
    };

    this.setTexture = function (slot, t, type)
    {
        if (this._textureslots[slot] != t)
        {
            this.gl.activeTexture(this.gl.TEXTURE0 + slot);
            this.gl.bindTexture(type || this.gl.TEXTURE_2D, t);
            this._textureslots[slot] = t;
        }
    };

    this.fullScreen = function ()
    {
        if (this.canvas.requestFullscreen) this.canvas.requestFullscreen();
        else if (this.canvas.mozRequestFullScreen) this.canvas.mozRequestFullScreen();
        else if (this.canvas.webkitRequestFullscreen) this.canvas.webkitRequestFullscreen();
        else if (this.canvas.msRequestFullscreen) this.canvas.msRequestFullscreen();
    };

    this.setSize = function (w, h)
    {
        this.canvas.style.width = w + "px";
        this.canvas.style.height = h + "px";

        this.canvas.width = w * this.pixelDensity;
        this.canvas.height = h * this.pixelDensity;

        this.updateSize();
    };

    this._resizeToWindowSize = function ()
    {
        this.setSize(window.innerWidth, window.innerHeight);
        this.updateSize();
    };

    this._resizeToParentSize = function ()
    {
        console.log("_resizeToParentSize");
        var p = this.canvas.parentElement;
        if (!p)
        {
            console.error("cables: can not resize to container element");
            return;
        }
        this.setSize(p.clientWidth, p.clientHeight);
        console.log("_resizeToParentSize", p.clientWidth, p.clientHeight);

        this.updateSize();
    };

    this.setAutoResize = function (parent)
    {
        window.removeEventListener("resize", this._resizeToWindowSize.bind(this));
        window.removeEventListener("resize", this._resizeToParentSize.bind(this));

        if (parent == "window")
        {
            window.addEventListener("resize", this._resizeToWindowSize.bind(this));
            window.addEventListener("orientationchange", this._resizeToWindowSize.bind(this));
            this._resizeToWindowSize();
        }
        if (parent == "parent")
        {
            window.addEventListener("resize", this._resizeToParentSize.bind(this));
            this._resizeToParentSize();
        }
    };

    this.printError = function (str)
    {
        var error = this.gl.getError();
        if (error != this.gl.NO_ERROR)
        {
            var errStr = "";
            if (error == this.gl.OUT_OF_MEMORY) errStr = "OUT_OF_MEMORY";
            if (error == this.gl.INVALID_ENUM) errStr = "INVALID_ENUM";
            if (error == this.gl.INVALID_OPERATION) errStr = "INVALID_OPERATION";
            if (error == this.gl.INVALID_FRAMEBUFFER_OPERATION) errStr = "INVALID_FRAMEBUFFER_OPERATION";
            if (error == this.gl.INVALID_VALUE) errStr = "INVALID_VALUE";
            if (error == this.gl.CONTEXT_LOST_WEBGL) errStr = "CONTEXT_LOST_WEBGL";
            if (error == this.gl.NO_ERROR) errStr = "NO_ERROR";

            console.log("gl error: ", str, error, errStr);
        }
    };

    this.saveScreenshot = function (filename, cb, pw, ph)
    {
        this.patch.renderOneFrame();

        var w = this.canvas.clientWidth;
        var h = this.canvas.clientHeight;

        if (pw)
        {
            this.canvas.width = pw;
            w = pw;
        }
        if (ph)
        {
            this.canvas.height = ph;
            h = ph;
        }

        function padLeft(nr, n, str)
        {
            return Array(n - String(nr).length + 1).join(str || "0") + nr;
        }

        var d = new Date();

        var dateStr = "".concat(String(d.getFullYear()) + String(d.getMonth() + 1) + String(d.getDate()), "_").concat(padLeft(d.getHours(), 2)).concat(padLeft(d.getMinutes(), 2)).concat(padLeft(d.getSeconds(), 2));

        if (!filename) filename = "cables_" + dateStr + ".png";
        else filename += ".png";

        this.patch.cgl.screenShot((blob) =>
        {
            this.canvas.width = w;
            this.canvas.height = h;
            if (blob)
            {
                var anchor = document.createElement("a");

                anchor.download = filename;
                anchor.href = URL.createObjectURL(blob);
                document.body.appendChild(anchor);

                anchor.click();
                if (cb) cb(blob);
                anchor.remove();
            }
            else
            {
                console.log("screenshot: no blob");
            }
        }, true);
    };
};

/**
 * push a matrix to the view matrix stack
 * @function pushviewMatrix
 * @memberof Context
 * @instance
 * @param {mat4} viewmatrix
 */
Context.prototype.pushViewMatrix = function ()
{
    this.vMatrix = this._vMatrixStack.push(this.vMatrix);
};

/**
 * pop view matrix stack
 * @function popViewMatrix
 * @memberof Context
 * @instance
 * @returns {mat4} current viewmatrix
 * @function
 */
Context.prototype.popViewMatrix = function ()
{
    this.vMatrix = this._vMatrixStack.pop();
};

Context.prototype.getViewMatrixStateCount = function ()
{
    return this._vMatrixStack.stateCounter;
};

/**
 * push a matrix to the projection matrix stack
 * @function pushPMatrix
 * @memberof Context
 * @instance
 * @param {mat4} projectionmatrix
 */
Context.prototype.pushPMatrix = function ()
{
    this.pMatrix = this._pMatrixStack.push(this.pMatrix);
};

/**
 * pop projection matrix stack
 * @function popPMatrix
 * @memberof Context
 * @instance
 * @returns {mat4} current projectionmatrix
 */
Context.prototype.popPMatrix = function ()
{
    this.pMatrix = this._pMatrixStack.pop();
    return this.pMatrix;
};

Context.prototype.getProjectionMatrixStateCount = function ()
{
    return this._pMatrixStack.stateCounter;
};

/**
 * push a matrix to the model matrix stack
 * @function pushModelMatrix
 * @memberof Context
 * @instance
 * @param {mat4} modelmatrix
 * @example
 * // see source code of translate op:
 * cgl.pushModelMatrix();
 * mat4.translate(cgl.mMatrix,cgl.mMatrix, vec);
 * trigger.trigger();
 * cgl.popModelMatrix();
 */
Context.prototype.pushMvMatrix = Context.prototype.pushModelMatrix = function ()
{
    // deprecated
    // var copy = mat4.clone(this.mMatrix);
    this.mMatrix = this._mMatrixStack.push(this.mMatrix);
};

/**
 * pop model matrix stack
 * @function popModelMatrix
 * @memberof Context
 * @instance
 * @returns {mat4} current modelmatrix
 */
Context.prototype.popMvMatrix = Context.prototype.popmMatrix = Context.prototype.popModelMatrix = function ()
{
    // todo: DEPRECATE
    // if (this._mMatrixStack.length === 0) throw "Invalid modelview popMatrix!";
    this.mMatrix = this._mMatrixStack.pop();
    return this.mMatrix;
};

/**
 * get model matrix
 * @function modelMatrix
 * @memberof Context
 * @instance
 * @returns {mat4} current modelmatrix
 */
Context.prototype.modelMatrix = function ()
{
    return this.mMatrix;
};

// state depthtest

Context.prototype._stackDepthTest = [];
Context.prototype.pushDepthTest = function (b)
{
    this._stackDepthTest.push(b);
    if (!b) this.gl.disable(this.gl.DEPTH_TEST);
    else this.gl.enable(this.gl.DEPTH_TEST);
};

Context.prototype.stateDepthTest = function ()
{
    return this._stackDepthTest[this._stackDepthTest.length - 1];
};

Context.prototype.popDepthTest = function ()
{
    this._stackDepthTest.pop();

    if (!this._stackDepthTest[this._stackDepthTest.length - 1]) this.gl.disable(this.gl.DEPTH_TEST);
    else this.gl.enable(this.gl.DEPTH_TEST);
};

// state depthwrite

Context.prototype._stackDepthWrite = [];
Context.prototype.pushDepthWrite = function (b)
{
    this._stackDepthWrite.push(b);
    this.gl.depthMask(b);
};

Context.prototype.stateDepthWrite = function ()
{
    return this._stackDepthWrite[this._stackDepthWrite.length - 1];
};

Context.prototype.popDepthWrite = function ()
{
    this._stackDepthWrite.pop();
    this.gl.depthMask(this._stackDepthWrite[this._stackDepthWrite.length - 1]);
};

// state depthfunc

Context.prototype._stackDepthFunc = [];

/**
 * enable / disable depth testing
 * like `gl.depthFunc(boolean);`
 * @function pushDepthFunc
 * @memberof Context
 * @instance
 * @param {Boolean} depthtesting
 */
Context.prototype.pushDepthFunc = function (f)
{
    this._stackDepthFunc.push(f);
    this.gl.depthFunc(f);
};

/**
 * current state of blend
 * @function stateDepthFunc
 * @memberof Context
 * @instance
 * @returns {Boolean} depth testing enabled/disabled
 */
Context.prototype.stateDepthFunc = function ()
{
    if (this._stackDepthFunc.length > 0) return this._stackDepthFunc[this._stackDepthFunc.length - 1];
    return false;
};

/**
 * pop depth testing and set the previous state
 * @function popDepthFunc
 * @memberof Context
 * @instance
 */
Context.prototype.popDepthFunc = function ()
{
    this._stackDepthFunc.pop();
    if (this._stackDepthFunc.length > 0) this.gl.depthFunc(this._stackDepthFunc[this._stackDepthFunc.length - 1]);
};

Context.prototype._stackBlend = [];

/**
 * enable / disable blend
 * like gl.enable(gl.BLEND); / gl.disable(gl.BLEND);
 * @function pushBlend
 * @memberof Context
 * @instance
 * @param {Boolean} blending
 */
Context.prototype.pushBlend = function (b)
{
    this._stackBlend.push(b);
    if (!b) this.gl.disable(this.gl.BLEND);
    else this.gl.enable(this.gl.BLEND);
};

/**
 * pop blend state and set the previous state
 * @function popBlend
 * @memberof Context
 * @instance
 */
Context.prototype.popBlend = function ()
{
    this._stackBlend.pop();

    if (!this._stackBlend[this._stackBlend.length - 1]) this.gl.disable(this.gl.BLEND);
    else this.gl.enable(this.gl.BLEND);
};

/**
 * current state of blend
 * @function stateBlend
 * @returns {boolean} blending enabled/disabled
 * @memberof Context
 * @instance
 */
Context.prototype.stateBlend = function ()
{
    return this._stackBlend[this._stackBlend.length - 1];
};

export const BLENDS = {
    BLEND_NONE: 0,
    BLEND_NORMAL: 1,
    BLEND_ADD: 2,
    BLEND_SUB: 3,
    BLEND_MUL: 4,
};

Context.prototype._stackBlendMode = [];
Context.prototype._stackBlendModePremul = [];

/**
 * push and switch to predefined blendmode (CONSTANTS.BLEND_MODES.BLEND_NONE,CONSTANTS.BLEND_MODES.BLEND_NORMAL,CONSTANTS.BLEND_MODES.BLEND_ADD,CONSTANTS.BLEND_MODES.BLEND_SUB,CONSTANTS.BLEND_MODES.BLEND_MUL)
 * @function pushBlendMode
 * @memberof Context
 * @instance
 * @param {Number} blendmode
 * @param {Boolean} premultiplied mode
 */
Context.prototype.pushBlendMode = function (blendMode, premul)
{
    this._stackBlendMode.push(blendMode);
    this._stackBlendModePremul.push(premul);

    const n = this._stackBlendMode.length - 1;

    this.pushBlend(this._stackBlendMode[n] !== CONSTANTS.BLEND_MODES.BLEND_NONE);
    this._setBlendMode(this._stackBlendMode[n], this._stackBlendModePremul[n]);
};

/**
 * pop predefined blendmode / switch back to previous blendmode
 * @function pushBlendMode
 * @memberof Context
 * @instance
 */
Context.prototype.popBlendMode = function ()
{
    this._stackBlendMode.pop();
    this._stackBlendModePremul.pop();

    const n = this._stackBlendMode.length - 1;

    this.popBlend(this._stackBlendMode[n] !== CONSTANTS.BLEND_MODES.BLEND_NONE);

    if (n > 0) this._setBlendMode(this._stackBlendMode[n], this._stackBlendModePremul[n]);
};

Context.prototype.glGetAttribLocation = function (prog, name)
{
    const l = this.gl.getAttribLocation(prog, name);
    if (l == -1)
    {
        // console.log("get attr loc -1 ",name);
        // debugger;
    }
    return l;
};

Context.prototype._setBlendMode = function (blendMode, premul)
{
    const gl = this.gl;

    if (blendMode == CONSTANTS.BLEND_MODES.BLEND_NONE)
    {
        // this.gl.disable(this.gl.BLEND);
    }
    else if (blendMode == CONSTANTS.BLEND_MODES.BLEND_ADD)
    {
        if (premul)
        {
            gl.blendEquationSeparate(gl.FUNC_ADD, gl.FUNC_ADD);
            gl.blendFuncSeparate(gl.ONE, gl.ONE, gl.ONE, gl.ONE);
        }
        else
        {
            gl.blendEquation(gl.FUNC_ADD);
            gl.blendFunc(gl.SRC_ALPHA, gl.ONE);
        }
    }
    else if (blendMode == CONSTANTS.BLEND_MODES.BLEND_SUB)
    {
        if (premul)
        {
            gl.blendEquationSeparate(gl.FUNC_ADD, gl.FUNC_ADD);
            gl.blendFuncSeparate(gl.ZERO, gl.ZERO, gl.ONE_MINUS_SRC_COLOR, gl.ONE_MINUS_SRC_ALPHA);
        }
        else
        {
            gl.blendEquation(gl.FUNC_ADD);
            gl.blendFunc(gl.ZERO, gl.ONE_MINUS_SRC_COLOR);
        }
    }
    else if (blendMode == CONSTANTS.BLEND_MODES.BLEND_MUL)
    {
        if (premul)
        {
            gl.blendEquationSeparate(gl.FUNC_ADD, gl.FUNC_ADD);
            gl.blendFuncSeparate(gl.ZERO, gl.SRC_COLOR, gl.ZERO, gl.SRC_ALPHA);
        }
        else
        {
            gl.blendEquation(gl.FUNC_ADD);
            gl.blendFunc(gl.ZERO, gl.SRC_COLOR);
        }
    }
    else if (blendMode == CONSTANTS.BLEND_MODES.BLEND_NORMAL)
    {
        if (premul)
        {
            gl.blendEquationSeparate(gl.FUNC_ADD, gl.FUNC_ADD);
            gl.blendFuncSeparate(gl.ONE, gl.ONE_MINUS_SRC_ALPHA, gl.ONE, gl.ONE_MINUS_SRC_ALPHA);
        }
        else
        {
            gl.blendEquationSeparate(gl.FUNC_ADD, gl.FUNC_ADD);
            gl.blendFuncSeparate(gl.SRC_ALPHA, gl.ONE_MINUS_SRC_ALPHA, gl.ONE, gl.ONE_MINUS_SRC_ALPHA);
        }
    }
    else
    {
        console.log("setblendmode: unknown blendmode");
    }
};

